// All components are in the range [0â€¦1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}


# define PI 3.14159
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = -0.5 + fragCoord/iResolution.xy;
    vec3 incam = (vec3(texture(iChannel0,uv+0.5)));
    vec3 hsv_incam = rgb2hsv(incam);
    uv *=1.0+sin(iTime*20.)*cos(hsv_incam[0]*2.*PI)*hsv_incam[1]*hsv_incam[2];
    //uv += hsv_incam.xy*0.01;
    vec3 incam_new = vec3(texture(iChannel0,uv+0.5));
    // Output to screen
    fragColor = vec4(incam_new,1.0);
}
