float noise( vec2 uv ) {
  return (fract(1.23424 + mod(uv.y, 3433.33) + sin(uv.x)* 1343788.23) + fract(sin(uv.y)*438909900.232) ) * 0.5;
}

mat2 rot( float a ) {
  return mat2( cos(a), -sin(a), sin(a), cos(a) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   // uv = 2. * uv - 1.;
      uv *= 1.5;

    // Time varying pixel color
    vec3 col = vec3( 0. );


    vec2 points[5];
    points[0] = vec2( 0.201, 0.722);
    points[1] = vec2( 0.421, 0.9322);
    points[2] = vec2( 0.91, 0.822);
    points[3] = vec2( 0.6321, 0.2002);
    points[4] = vec2( 0.5, 0.672);

    float min_d = 100000. ;

uv.x += sin(iTime) / 2.;
    float j = 1. ;
    for ( int i = 0; i < 5; i ++) {

     points[i].x += sin( iTime) / j  ;

        points[i].y += 0.2 * cos( iTime) / j   ;

      min_d = min( min_d, distance( uv , points[i] ) );
        j += 1. ;
    }
    // Output to screen
    float vor = smoothstep( 0.1, 0.4, min_d )  ;


    uv *= 2. ;
    uv *= rot( iTime / 4.);
    vec2 t1 = floor( uv);

	vec2 t2 = fract( uv );

    float tiles = t2.x / t2.y * abs(  sin(  atan( iTime / 2. )) );

    col.x = vor;
    col.z = tiles  ;

    //col = vec3( vor);
    fragColor = vec4(col,1.0);
}
