float line(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return smoothstep(2.0 / iResolution.y, 0.0, length(pa - ba * h) - 0.005);
}

float circle(in vec2 p, in vec2 o, in float r) {
    return smoothstep(2.0 / iResolution.y, 0.0, length(p - o) - r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y;
    vec3 color = vec3(0.0, 0.0, 0.0);
    for (float i=0.0; i < 1.1; i += 0.1) {
        float tractrixLine = line(uv, vec2(0.0, 1.0 - i), vec2(i, 0.0));
        color += tractrixLine;
    }

    vec2 pursueePos = vec2(fract(0.25 * iTime), 0.0);
    vec2 pursuerPos = mix(vec2(0.0, 1.0 - pursueePos.x), pursueePos, pursueePos.x);

    float pursuitLine = line(uv, pursueePos, pursuerPos);
    float pursuee = circle(uv, pursueePos, 0.04);
    float pursuer = circle(uv, pursuerPos, 0.04);

    color = pursuitLine > 0.0 ? vec3(0.0, pursuitLine, 0.0) : color;
    color = pursuee > 0.0 ? vec3(0.0, 0.0, pursuee) : color;
    color = pursuer > 0.0 ? vec3(pursuer, 0.0, 0.0) : color;

    fragColor = vec4(color, 1.0);
}
