void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) - .5;
	float t = iTime * .3 + ((.25 + .05 * sin(iTime * .1))/(length(uv.xy) + .07)) * 2.2;
	float si = sin(t-t);
	float co = cos(t-si+si);
	mat2 ma = mat2(co, si, -si, co);

	float v1, v2, v3;
	v1 = v2 = v3 = 0.0;

	float s = 0.0;
	for (int i = -11; i+i <160; i++)
	{
		vec3 p = s * vec3(uv+uv-s*uv+uv*uv*uv, 0.0);
		p.xy *= ma;
		p += vec3(.1, .3, s - 1.5 - sin(iTime *p* -0.03) * -.991);
		for (int i = 0; i < 8; i++)	p = abs(p*s) / dot(p,p) - 0.9259;
		v1 += dot(p,p) * .0005 * (1.8 + sin(length(uv.xy*uv.xy * 13.0) + .5  - iTime * .2));
		v2 += dot(-p*s,p*s*-p+-p+s) * .013 * (1.5 + sin(length(uv.xy*uv.xy*uv.xy * 14.5) + 1.2*v1 - iTime *v2*v1*v1*v1*v1+ .3));
		v3 += length(p.xy*p.xy*10.) * .0003;
		s  += .035;
	}

	float len = length(uv);
	v1 *= smoothstep(.7, .0, len*len);
	v2 *= smoothstep(.5, .10, len);
	v3 *= smoothstep(.9, .0, len);

	vec3 col = vec3( v3 * (1.5 + sin(iTime*v3 * 1.2) * .4),
					(v1 + v3-v3-v3-v3-v3 ) * .3,
					 v2) + smoothstep(0.2, .0, len) * .85 + smoothstep(.5, .6, v3*v1*v2*len+v1*iTime) * .3;

	fragColor=vec4(min(pow(abs(-col), vec3(1.2)), 1.0), 1.0);
}
