const float PI = radians(180.);
const float TAU = radians(360.);
const float HELICOIDLENGTH = 1.5;
const float NBTUBES = 3.;
const float MAXDIST = 15.;


vec2 minVecSelect(vec2 a, vec2 b)
{
    return a.x<b.x?a:b;
}

float sdcircle2d(vec2 p, float r)
{
     return length(p)-r;
}

vec2 rotate(vec2 center, float angle, float radius)
{
    return center + vec2(cos(angle),-sin(angle)) * radius;
}

vec2 slice(vec3 v)
{
    float	time = (v.z/TAU)*2.+iTime*0.1;

    float	r1 = 0.04*sin(v.z*4.+iTime*7.) + 0.08;

    float   r2 = 0.15 + sin(v.z + iTime)*0.05;

    vec2	m = vec2(sdcircle2d(rotate(v.xy,time*TAU,r2),r1));

    for(float i = 1. ; i < NBTUBES ; i++)
    {
        m = minVecSelect(m,vec2(sdcircle2d(rotate(v.xy,(time+i/NBTUBES)*TAU,r2),r1),i));
    }
    return m;
}


vec2 curve(float z)
{
    return vec2(cos(z*.2)*cos(z*0.5),cos(z*.3)*cos(z*0.7));
}

vec3 map(vec3 p)    
{
    p.z += iTime;

    p.xy += curve(p.z);

    float angle = atan(p.y,p.x);

    p.z+= (angle/TAU)*HELICOIDLENGTH;

    p.z = mod(p.z+HELICOIDLENGTH/2.,HELICOIDLENGTH)-HELICOIDLENGTH/2.;

    vec3 v = vec3(length(p.xy)-1., p.z, angle);

    return vec3(slice(v),angle*4.+iTime*7.);
}


// From IQ
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

vec3 CalcNormal(vec3 pos)
{
    vec2	eps	= vec2(0.01,0);
	float	d	= map(pos).x;
	return	normalize(vec3(map(pos+eps.xyy).x-d,map(pos+eps.yxy).x-d,map(pos+eps.yyx).x-d));
}

vec3 rayMarch(vec3 ro, vec3 rd, out float t, float maxDist)
{
    vec3	d;
    t = 0.5;
    for(int i=0;i<75;++i)
    {
        d = map(ro+rd*t);
        t+=d.x*.7;
        if (d.x < .001 || t > maxDist) break;
    }
    return d;
}

vec3 applyFog(vec3 rgb,float distance, vec3 fogColor)
{
    float fogAmount = 1.0 - exp( -distance*0.2 );
    return mix( rgb, fogColor, fogAmount );
}

vec3 render(vec3 ro,vec3 rd)
{
    float	t;

    vec3	r = rayMarch(ro,rd,t,MAXDIST);

    vec3	col;

    if (t < MAXDIST)
    {
    	vec3 p = ro+rd*t;
        vec3 n = CalcNormal(p);
		col = mix(vec3(1.,0.75,.5),vec3(1.,0.55,.3),0.5*sin(r.z*5.)+0.5);
		col = mix(col,vec3(1),0.2*sin(r.y*TAU/NBTUBES)+0.2)*mix(n.y,1.,0.6);
		vec3 reflected=reflect(rd,n);
        vec3 env = texture(iChannel0,reflected).xyz;
		col = mix(col,env,length(env)*0.3);
		col*= calcAO(p,n);
		col = applyFog(col,t,vec3(0));
    }
    else
    {
        col = vec3(0);
    }

    return col;
}

mat3 buildLookAtMatrix( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{   
    mat3 ma = buildLookAtMatrix(vec3(0), vec3(0,0.25,-1));

    fragColor = vec4(render(ma*ro/4.-vec3(curve(iTime),0),ma*rd) ,1);
}

//#define AA

void mainImage(out vec4 fragColor, vec2 fragCoord)
{

	vec3 tot = vec3(0.0);

#ifdef AA
	vec2 rook[4];
    rook[0] = vec2( 1./8., 3./8.);
    rook[1] = vec2( 3./8.,-1./8.);
    rook[2] = vec2(-1./8.,-3./8.);
    rook[3] = vec2(-3./8., 1./8.);
    for( int n=0; n<4; ++n )
    {
        // pixel coordinates
        vec2 o = rook[n];
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else //AA
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif //AA        
        // camera	
        vec3 ro = vec3(-curve(iTime),0);
        vec3 ta = vec3(-curve(iTime + 2.),5);
        // camera-to-world transformation
        mat3 ma = buildLookAtMatrix( ro, ta );

        vec3 rd =  ma*normalize(vec3(p,2.0));        

        vec3 col = render(ro ,rd);

	    tot += col;
#ifdef AA
    }
    tot /= 4.;
#endif

	fragColor = vec4( tot, 1.0 );    
}
