vec4 RGBAToColor(const int color)
{
    return vec4(float((color >> 24) & 0xFF) / 255.0, float((color >> 16) & 0xFF) / 255.0, float((color >> 8) & 0xFF) / 255.0, float(color & 0xFF) / 255.0);
}

bool IsABlock(const uint xIndex, const uint yIndex, const uint tetriminoIndex)
{
    return (tetriminoes[tetriminoIndex / 2U] & int(0x1U << (((tetriminoIndex % 2U) * 16U) + (yIndex * tetriminoWidth) + xIndex))) > 0;
}

int GetBlockRGBA(const uint xIndex, const uint yIndex, const uint blockIndex)
{
    return tetriminoBlocksColors[blockIndex][(tetriminoBlockColorIndices >> ((yIndex * 8U) + (xIndex * 2U))) & 0x3U];
}

vec4 Tetrimino(const uint tetriminoIndex, const uint blockIndex, const uint rotation, const vec2 uv)
{
    vec4 ret = vec4(0.0);
    vec2 flipped_uv = vec2(uv.x, 1.0 - uv.y);
    if ((uv.x >= 0.0) && (uv.x < 1.0) && (uv.y >= 0.0) && (uv.y < 1.0))
    {
        uint x_index = uint(flipped_uv.x * float(tetriminoWidth));
        uint y_index = uint(flipped_uv.y * float(tetriminoHeight));
        uint temp;
        switch (rotation % 4U)
        {
            case 1U:
            	temp = tetriminoHeight - y_index - 1U;
            	y_index = x_index;
            	x_index = temp;
            	break;
            case 2U:
            	x_index = tetriminoWidth - x_index - 1U;
            	y_index = tetriminoHeight - y_index - 1U;
            	break;
            case 3U:
            	temp = y_index;
            	y_index = tetriminoWidth - x_index - 1U;
            	x_index = temp;
            	break;
        }
        if (IsABlock(x_index, y_index, tetriminoIndex))
        {
        	vec2 block_uv = vec2(fract(flipped_uv.x * float(tetriminoWidth)), fract(flipped_uv.y * float(tetriminoHeight)));
            uint block_x_index = uint(block_uv.x * float(tetriminoBlockWidth));
            uint block_y_index = uint(block_uv.y * float(tetriminoBlockHeight));
            ret = RGBAToColor(GetBlockRGBA(block_x_index, block_y_index, blockIndex));
        }

    }
    return ret;
}

int Random(const int seed)
{
    return (843314861 * seed + 453816693) % 1073741824;
}

vec4 AlphaBlend(const vec4 bottom, const in vec4 top)
{
    float alpha = top.a + bottom.a * (1.0 - top.a);
    return vec4((top.rgb * top.a + bottom.rgb * bottom.a * (1.0 - top.a)) / alpha, alpha);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 view_uv = fragCoord / iResolution.y;
    vec2 uv = ((fragCoord - (iResolution.xy * 0.5)) * 10.0) / iResolution.y;
    vec2 world_uv = vec2(uv.x + 100.25 + (sin(iTime * steerFrequency * doublePi) * steerAmplitude * 0.5), uv.y + 100.0 + (iTime * fallSpeed));
    vec2 tetrimino_uv = fract(world_uv) * 1.5f;
    int random = Random(int(world_uv.x) + (int(world_uv.y) * int(numTetriminoes)));
    fragColor = vec4(AlphaBlend(mix(vec4(0.0, 0.0625, 0.125, 1.0), vec4(0.0, 0.0, 0.0, 1.0), view_uv.y), Tetrimino(uint(abs(random)) % numTetriminoes, uint(abs(random)) % numTetriminoBlocks, uint(abs(random) + int((((random % 2) == 0) ? iTime : -iTime) * turnSpeed)) % 4U, tetrimino_uv)).xyz, 1.0);
}

