float Circle (vec2 uv, float blur, float r) {
    float d = length(uv);
    float c = smoothstep ( r, r-blur,d);
    return c;
}

float Circle_line(vec2 uv, float r, int num_segments){
    float x = uv.x;
    float y = uv.y;
    float c = 0., len = 0.1, thick=0.001;

    r/= float(num_segments);

    for (int i = 0; i < num_segments;i++) {
        float theta = 2.0f * 3.1415926f * float(i) / float (num_segments);
        x += r * cos(theta);
        y += r * sin(theta);
        c += Circle(vec2(x,y-0.1),.001,.005);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float c = 0.;
    vec2 pos;
    vec3 col;

    float x = uv.x;
    float y = uv.y;
    y+=0.5;

    float g = 9.8, h = .1;
    float vel = sqrt(2.*g*h);
    float t = sqrt(2.* h/g);
    float Vo = 0., Vt = Vo+g*t,Vt2 = 0.;

    h = Vt*Vt/ (2.0* g);

    float m = -(x-.5)*(x+.5);
    float a =.5, f=1.,t1 = 1.;
    m = abs(1.5*(sin(iTime)*pow(2.718,-iTime/2.)));
    pos=vec2(0.,0.0);
    c = Circle_line(vec2(x,y-m)-pos,.3, 50);

    col = vec3(1.,1.,0.) * c;
    // Output to screen
    fragColor = vec4(col,1.0);
}
