vec2 force (vec4 a, vec4 b) {
    vec2 r = b.xy-a.xy, v = b.zw-a.zw;
    float l = length(r);
    if (l < 1e-4) return vec2(0);
    vec2 f = vec2(0);
    if (l!=0.){
    f += 10.*(1.-dot(a.zw,b.zw))*r/max(1.,l*l*l);
    f -= 600.*r/max(1.,l*l*l*l*l*l);
    }

    if (length(f)>5.) return 5.*normalize(f);
    return f;
}

float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return (length(p-a-(b-a)*i))+1e-3*length(p-b);
}float line (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	float l = (length(p-a-(b-a)*i));
	l -= .5;
    if (l>0.) return l;
    else return -1e4+abs(length(a-b)-length(a-p));
}

