// SORT TRIANGULATION
void Y (inout vec4 Q, inout vec4 a, inout vec4 aa, inout vec4 aaa, inout vec4 aaaa, float i, vec2 U) {
	vec4 n = A(i);
    float ln = length(n.xy-U),
          la = length(a.xy-U);
    if (ln<la) {
    	Q.x = i;
        a = n;
    }
    float pn = sg(U,a.xy,n.xy),
          pa = sg(U,a.xy,aa.xy);
    if (pn<=pa){
        aa = n;
        Q.y = i;
        pa = pn;
    }
    float sn = pn,
          sa = sg(U,a.xy,aaa.xy);
    if (sn<sa&&sn>pa) {
    	aaa = n;
        Q.z = i;
        sa=sn;
    }
    float tn = sn,
          ta = sg(U,a.xy,aaaa.xy);
    if (tn<ta&&tn>sa) {
    	aaaa = n;
        Q.z = i;
    }
}
void X (inout vec4 Q, inout vec4 a, inout vec4 aa, inout vec4 aaa, inout vec4 aaaa,  vec2 U, vec2 r) {
    vec4 b = B(U+r);
    Y(Q,a,aa,aaa,aaaa,b.x,U);
    Y(Q,a,aa,aaa,aaaa,b.y,U);
    Y(Q,a,aa,aaa,aaaa,b.z,U);
    Y(Q,a,aa,aaa,aaaa,b.w,U);
}
