mat2 rot(float a) {
    return mat2( cos(a), -sin(a), sin(a), cos(a) );
}

#define width (1./iResolution.y)

// Mix so that c_neg is on negative, c_pos on positive values of d.
// The mix happens from -w to +w.
float pmmix(float d, float c_neg, float c_pos) {
	return mix(c_neg, c_pos, clamp(d/(2.0*width)+0.5, 0.0, 1.0));
}

float yingyang(vec2 uv, float ang) {
    float r = 0.5;
    float ac = 0.;
    float s = 1.;
    float c=0.5;

    while( r > width ) {
	    uv *= rot(ang);
        c = pmmix(length(uv) - 2. * r, float(s*uv.x > 0.), c);

        vec2 a = uv - vec2(0, r),
             b = uv - vec2(0,-r);
        float la = length(a);
        float lb = length(b);

        if (min(la, lb) > r + width) return c;


        if (uv.x > 0.) {
		    c = pmmix(la-r, 1.-ac, c);
        } else {
		    c = pmmix(lb-r, ac, c);
        }

        if (uv.y > 0.) {
            uv = a;
        } else {
            uv = b;
        }

        s *= -1.;
        ac = 1. - ac;
        r /= 2.0;
        ang *= -sqrt(2.);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y;

    float col = yingyang( U, iTime);

    fragColor = vec4(vec3(col), 1.0);
}
