// variant of https://shadertoy.com/view/wlBcRR

void mainImage(out vec4 O,  vec2 u ){

    vec2 R = iResolution.xy,  I,
         U = (u - .5*R) / R.y,                                // normalized coordinates
	     z = U - vec2(-1,0);  U.x -= .5;                      // Moebius transform
#if 1
    U *= mat2(z,-z.y,z.x) / dot(U,U);             
                  // offset   spiral, zoom   phase            // spiraling
    U =   log(length(U+=.5))*vec2(.5, -.5) + iTime/8.
        + atan(U.y, U.x)/6.283 * vec2(6, 1); // n    
#else
    U.x +=.022; U *= vec2(1.5,1);                             // test in the plane
    if(u.x/R.y>sqrt(3.)/2.) return;
#endif
  //U *= vec2(10,14.82)/1.43;// /(.5+iMouse.x/R.x);
    U *= vec2(21./3., 2./sqrt(3.)*9.);                        // thanks Shane !
    U *= mat2(1,0,.5,sqrt(3.)/2.);                            // triangular mesh
    z = fwidth(U);
    I = floor(U); U = fract(U); 
    float s = float(U.x-U.y>0.), v,                           // parallelogram = 2 triangles
          i = mod( I.x + I.y , 3.), id; 
    I += i==0.? vec2(0) : i==1. ? vec2(1) : vec2(s,1.-s);  id = I.x +7.81*I.y;
    vec4 V = vec4( 1.-2.*abs(U.x-U.y) , abs(U*2.-1.) , 0 );
    v = V[i<2. ? 2-int(i+s)%2 : 0 ];
    U = i==0. ? U : i==1. ? 1.-U : s==0.?vec2(U.x,U.y-1.):vec2(1.-U.x,-U.y);
    U *= inverse(mat2(1,0,.5,sqrt(3.)/2.));

    O  = smoothstep(.7,-.7,(length(U)-.95)/( abs(z.x-z.y)>1.?.1:z.y*2.))
        * (.6 + .6 * cos( id  + vec4(0,23,21,0)  ) );         // color
    if (fract(iTime/6.)<.4) O += smoothstep(-.7,.7, (v-.95)/( abs(z.x-z.y)>1.?.1:z.y*4.));  // draw AA tiles
}
