#define rnd(x) fract(54321.987 * sin(987.12345 * x))
mat2 rot(float a){float s=sin(a),c=cos(a);return mat2(c,-s,s,c);}


float boxSize = .3;
vec3 p;
float id;

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}


float dist(vec3 pv){
    float cont = sdRoundBox(pv,vec3(boxSize*3.-.01),0.);
    id = length(floor(pv*boxSize*11.)*vec3(10.,100.,1000.));
    pv=mod(pv,boxSize)-boxSize/2.;
    float boxes = -sdRoundBox(pv,vec3(boxSize/2.-.001),0.);
    p=pv;
    return max(boxes, cont);
}

vec3 norm(vec3 p){
    vec2 e = vec2(.0001,0.);
	return normalize(vec3(
    	dist(p+e.xyy)-dist(p-e.xyy),
    	dist(p+e.yxy)-dist(p-e.yxy),
        dist(p+e.yyx)-dist(p-e.yyx)
    ));
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

