        p=rd*d-vec3(.0,.0,5.);
        mat2 rotXZ = rot(iTime);
        mat2 rotXY = rot(iTime);
        p.xz*=rotXZ;
        p.xy*=rotXY;
        d+=e=dist(p);
    }

