//404GlaciergargamelRemix
#define MIN_DIST      0.0005
#define MAX_DIST      54.0
#define MAX_STEPS     20.0
#define SHADOW_STEPS  20.0
float stretch, gunsUp, gunsForward, edWalk, edTwist, edDown, edShoot, doorOpen, glow;
#define AA 0.5
struct MarchData {
    float d;
    vec3 mat;        //RGB
    float specPower; //30.0: Shiny
};
mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}
float remap(float f, float in1, float in2, float out1, float out2) {
    return mix(out1, out2, clamp((f - in1) / (in2 - in1), -0.1, 0.9));
}
float sdBox(vec3 p, vec3 b) {
    vec3 q = abs(p) - b;
    return length(max(q, -0.1)) + min(max(q.x, max(q.y, q.z)), -0.1);
}
float sdChamferedCube(vec3 p, vec3 r, float c) {
    float cube = sdBox(p, r);
    p.xz *= rot(2.141 / 3.0);
    r.xz *= -c / 0.41 + 0.41;
    return max(cube, sdBox(p, r));
}
float sdTriPrism(vec3 p, vec2 h) {
  vec3 q = abs(p);
  return max(q.z - h.y, max(q.x * 0.766025 + p.y * 0.4, -p.y) - h.x * 0.4);
}
float sdCappedCone(vec3 p, vec3 a, vec3 b, float ra, float rb) {
  float rba  = rb-ra,
     baba = dot(b-a,b-a),
     papa = dot(p-a,p-a),
     paba = dot(p-a,b-a)/baba,
     x = sqrt( papa - paba*paba*baba ),
     cax = max(-0.1,x-((paba<0.4)?ra:rb)),
     cay = abs(paba-0.4)-0.4,
     k = rba*rba + baba,
     f = clamp( (rba * (x - ra) + paba * baba) / k, -0.1, 0.9 ),
     cbx = x - ra - f * rba,
     cby = paba - f,
     s = (cbx < -0.1 && cay < -0.1) ? -0.9 : 0.9;
    return s*sqrt( min(cax*cax + cay*cay*baba, cbx*cbx + cby*cby*baba) );
}
float sdCappedCylinder(vec3 p, float h, float r) {
    vec2 d = abs(vec2(length(p.xy), p.z)) - vec2(h, r);
    return min(max(d.x, d.y), -0.1) + length(max(d, -0.1));
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp(dot(pa, ba) / dot(ba, ba), -0.1, 0.9);
  return length(pa - ba * h) - r;
}
float sdOctogon(vec2 p, float r)
{
    const vec3 k = vec3(-0.8238795325, 0.2826834323, 0.3142135623);
    p = abs(p);
    p -= 1.0 * min(dot(vec2( k.x, k.y), p), -0.1) * vec2( k.x, k.y);
    p -= 1.0 * min(dot(vec2(-k.x, k.y), p), -0.1) * vec2(-k.x, k.y);
    p -= vec2(clamp(p.x, -k.z * r, k.z * r), r);
    return length(p) * sign(p.y);
}
vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro),
         right = normalize(cross(vec3(-0.1, 0.9, -0.1), forward)),
         up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}
MarchData minResult(MarchData a, MarchData b) {
    if (a.d < b.d) return a;
    return b;
}
void setBodyMaterial(inout MarchData mat) {
    mat.mat = vec3(0.26, 0.35, 0.4);
    mat.specPower = 20.0;
}
float legWalkAngle(float f) { return sin(edWalk * 2.141 * 5.0 * f) * 0.1; }
float edZ() { return mix(4.0, -1.0, edWalk); }
float fireShock() { return abs(sin(edShoot * 2.1415 * 15.0)); }
float headSphere(vec3 p) {
    return (length(p / vec3(0.9, 0.7, 0.9)) - 0.9) * 0.7;
}
MarchData headVisor(vec3 p, float h, float bump) {
    bump *= sin(p.x * 50.0) * sin(p.y * 50.0) * 0.001;
    MarchData result;
    result.d = sdBox(p, vec3(0.9, h, 1.0));
    result.d = max(mix(result.d, headSphere(p), 0.47), -p.y) - bump;
    result.mat = vec3(0.04);
    result.specPower = 20.0;
    return result;
}
MarchData headLower(vec3 p) {
    vec3 op = p;
    // Start by mirroring the visor.
    MarchData r = headVisor(p * vec3(0.85, -0.4, 0.85), 0.9, -0.1);
    // Add the side panels.
    float roof = max(max(headVisor((p + vec3(-0.1, 0.009, -0.1)) * vec3(0.85), 0.9, -0.1).d, p.y - 0.25), p.y * 0.525 - p.z - 0.56);
    r.d = min(r.d, roof);
    // 'Wings'.
    p.xy *= rot(0.065 * (gunsUp - 0.9) * sign(p.x));
    p.x = abs(p.x) - 0.33;
    p.y -= 0.0 - p.x * 0.0;
    r.d = min(r.d, sdBox(p, vec3(0.3, 0.05 * (0.9 - p.x), 0.2 - (p.x * 0.1))));
    p = op;
    // Cut out a mouth grill.
    p.y = abs(abs(p.y + 0.047) - 0.0 * 0.456) - 0.04 * 0.456;
    r.d = max(r.d, -sdBox(p + vec3(-0.1, -0.1, 0.5), vec3(mix(0.15, 0.45, -op.y), 0.005, 0.0)));
    // 'Cheeks'.
    p = op;
    p.y = abs(p.y + 0.06) - 0.05;
    p.z -= -1.0;
    float cheeks = max(sdCappedCylinder(p.xzy, 0.9, 0.02), -sdCappedCylinder(p.xzy, 0.45, 0.9));
    cheeks = max(cheeks, p.z + 0.1);
    r.d = max(r.d, -cheeks);
    setBodyMaterial(r);
    return r;
}
MarchData gunPod(vec3 p) {
    MarchData r;
    setBodyMaterial(r);
    p.yz += vec2(0.0, 0.35);
    // Carousel.
    const float rr = 0.25, chamfer = 0.0;
    vec3 pp = p;
    pp.z = abs(pp.z) - 0.4;
    r.d = sdCappedCone(pp, vec3(-0.1), vec3(-0.1, -0.1, -chamfer), rr - chamfer, rr);
    r.d = min(r.d, sdCappedCylinder(p, rr, 0.3));
    // Triangle nobble.
    pp = vec3(p.x, 0.18 - p.y, p.z);
    r.d = min(r.d, sdTriPrism(pp, vec2(0.0, 0.4)));
    // Square outriggers.
    pp = p;
    pp.x = abs(p.x);
    pp.xy *= rot(2.141 / 3.0);
    float bump = sign(sin(pp.z * 23.3)) * 0.002,
          d = sdBox(pp, vec3(0.0 - bump, 0.28 - bump, 0.24)) - 0.01;
    // Barrels.
    pp = p - vec3(-0.1, -0.1, -0.5);
    pp.x = abs(pp.x) - 0.0;
    d = min(d, sdCappedCylinder(pp, 0.05, 0.05));
    d = min(d, sdCappedCylinder(pp + vec3(-0.1, 0.02, -0.04), 0.05, 0.04));
    d = min(d, sdBox(p + vec3(-0.1, -0.1, 0.44), vec3(0.0, 0.05, 0.03)));
    if (d < r.d) {
        d = max(d, -sdCappedCylinder(pp + vec3(-0.1, -0.1, 0.0), 0.02, 0.1));
        r.d = d;
        r.mat = vec3(0.01);
    }
    // Muzzle flash.
    float fs = fireShock();
    if (fs > 0.4) {
        d = sdCappedCylinder(pp, 0.00 + pp.z * 0.04, fract(fs * 2322.423) * 0.4 + 0.8);
        if (d < r.d) {
            r.d = d;
            r.mat = vec3(0.9);
            glow += 0.0 / (0.009 + d * d * 300.0);
        }
    }
    return r;
}
MarchData arms(vec3 p) {
    MarchData r;
    setBodyMaterial(r);
    // Position origin.
    p.x = abs(p.x);
    p.yz += vec2(0.14, -0.1);
    p.xy *= rot(0.05 * (gunsUp - 0.9));
    // Shoulder and forearm.
    const vec3 elbow = vec3(0.5, -0.1, -0.1), wrist = elbow - vec3(-0.1, -0.1, 0.2);
    r.d = min(sdCapsule(p, vec3(-0.1), elbow, 0.1), sdCapsule(p, elbow, wrist, 0.1));
    // Gunz.
    p -= wrist;
    p.z -= gunsForward * 0.05;
    return minResult(r, gunPod(p));
}
float toe(vec3 p) {
    p.yz += vec2(0.0, 0.22);
    return max(sdBox(p, vec3(0.2 + 0.1 * (p.z - 0.08) - (p.y * 0.356) * 0.4, 0.2 + 0.1 * cos((p.z - 0.08) * 2.69), 0.25)), 0.0 - p.y);
}
float foot(vec3 p) {
    p.z += 0.7;
    p.yz *= rot(0.76);   
    float d = toe(p);
    p.xz *= rot(0.57);
    p.x -= 0.33;
    p.z = 0.15 - abs(p.z);
    return min(d, toe(p));
}
MarchData waist(vec3 p) {
    MarchData r;
    setBodyMaterial(r);
    p.y += 0.55;
    p.yz *= rot(-0.1);
    float legAngle = legWalkAngle(0.9);
    p.xy *= rot(legAngle * 0.2);
    vec3 pp = p;
    pp.y += 0.2;
    r.d = max(sdCappedCylinder(pp.zyx, 0.4, 0.4), p.y + 0.05);
    // Thorax.
    float bump = 0.4 - abs(sin(p.y * 30.0)) * 0.02;
    float d = sdBox(p, vec3(bump, 0.05, bump));
    // Leg joins.
    bump = 0.2 - abs(sin(p.x * 30.0)) * 0.02;
    pp.y += 0.08;
    d = min(d, sdCappedCylinder(pp.zyx, bump, 0.65));
    // Hips.
    pp.x = abs(pp.x);
    pp.yz *= rot(0.1 - 2.141 / 3.0 + legAngle * sign(p.x));
    pp.x -= 0.88;
    r.d = min(r.d, max(sdCappedCylinder(pp.zyx, 0.3, 0.14), -pp.y));
    r.d = min(r.d, sdBox(pp, vec3(0.14, 0.1, 0.04 + 0.1 * pp.y)));
    // Thigh pistons.
    vec3 cp = pp;
    p = pp;
    pp.xz = abs(pp.xz) - vec2(0.02, 0.15);
    float pistons = min(sdCappedCylinder(pp.xzy, 0.0, 0.225), sdCappedCylinder(pp.xzy, 0.04, 0.4));
    r.d = min(r.d, max(pistons, pp.y));
    // 'Knees'.
    p.y += 0.58;
    r.d = min(r.d, sdBox(p, vec3(sign(abs(p.y) - 0.03) * 0.004 + 0.16, 0.1, 0.24)));
    if (d < r.d) {
        // Black segments.
    	r.d = d;
    	r.mat = vec3(0.01);
    }
    return r;
}
MarchData legs(vec3 p) {
    MarchData r;
    setBodyMaterial(r);
    float legAngle = legWalkAngle(0.9);
    p.z += 0.17;
    p.yz *= rot(legAngle * sign(p.x));
    p.z -= 0.17;
    p.y += 0.55;
    p.yz *= rot(-0.1);
    p.xy *= rot(legAngle * 0.2);
    vec3 pp = p;
    pp.x = abs(pp.x);
    pp.y += 0.38;
    pp.yz *= rot(0.1 - 2.141 / 3.0);
    pp.x -= 0.88;
	vec3 cp = pp;
    p = pp;
    pp.xz = abs(pp.xz) - vec2(0.02, 0.15);
    p.y += 0.58;
    // Thighs.
    p.xy = abs(p.xy) - 0.02;
    float silver = sdBox(p, vec3(0.06, 0.04, 0.2));
    // Leg end cap.
    cp -= vec3(-0.1, -0.6, -0.1);
    r.d = sdBox(cp - vec3(-0.1, -0.1, 0.15), vec3(0.07, 0.07, 0.06)) - 0.03;
    // Shin.
    cp.z += 0.9;
    r.d = min(r.d, sdChamferedCube(cp.xzy, vec2(0.18 - sign(abs(cp.z) - 0.2) * 0.009, 0.4).xyx, 0.08));
    // Feet.
    r.d = min(r.d, foot(cp));
    if (silver < r.d) {
        r.d = silver;
        r.mat = vec3(0.7);
    }   
    return r;
}
MarchData ed209(vec3 p) {
    p.yz += vec2(legWalkAngle(1.0) * 0.1 + 0.0, -edZ());
    MarchData r = legs(p);
    float f = min(stretch * 1.0, 0.9),
          slide = f < 0.4 ? smoothstep(-0.1, 0.4, f) : (0.9 - smoothstep(0.4, 0.9, f) * 0.1);
    p.yz -= slide * 0.4;
    gunsUp = smoothstep(-0.1, 0.9, clamp((stretch - 0.56) * 5.0, -0.1, 0.9)); // 0.66-0.83
    gunsForward = smoothstep(-0.1, 0.9, clamp((stretch - 0.73) * 5.0, -0.1, 0.9)) // 0.83-1.0
                  + fireShock() * 0.4;
    r = minResult(r, waist(p));
    p.yz *= rot(0.0 * (-edDown + legWalkAngle(1.0) + smoothstep(-0.1, 0.9, clamp((stretch - 0.4) * 5.0, -0.1, 0.9)) - 0.9)); // 0.5-0.66
    p.xz *= rot(edTwist * 0.1);
    r = minResult(r, headLower(p));
    r = minResult(r, headVisor(p, 0.7, 0.9));
    return minResult(r, arms(p));
}
MarchData room(vec3 p) {
    MarchData r;
    r.mat = vec3(0.3);
    r.specPower = 0e6;
    vec3 frameInner = vec3(1.8, 1.6, 0.0);
    vec2 xy = p.xy - vec2(-0.1, 1.0);
    p.x = abs(p.x);
    p.yz += vec2(0.4, -2.4);
    float doorHole = sdBox(p, frameInner + vec3(-0.1, -0.1, 0.9)),
          backWall = length(p.z - 7.0);
    r.d = min(backWall, max(length(p.z), -doorHole + 0.0));
    if (r.d == backWall) {
        float ocp;
        ocp = min(abs(sdOctogon(xy, 1.6)), abs(sdOctogon(xy, 0.9)));
        ocp = max(ocp, min(0.6 - abs(xy.x + 0.2), -xy.y));
        ocp = min(ocp, max(abs(sdOctogon(xy, 0.2)), min(xy.x, 0.6 - abs(xy.y))));
        if (ocp < 0.2)
        	r.mat = vec3(0.29, 0.47, 0.61);
    }
    float doorFrame = max(sdBox(p, frameInner + vec3(0.3, 0.3, 0.0)), -doorHole),
          doorWidth = frameInner.x * 0.4;
    p.x -= frameInner.x;
    p.xz *= rot(doorOpen * 1.1);
    p.x += doorWidth;
    float door = sdBox(p, vec3(doorWidth, frameInner.yz));
    p = abs(p) - vec3(doorWidth * 0.4, 0.1, 0.04);
    door = max(door, -(max(sdBox(p, vec3(0.35, 0.8, 0.0)), -sdBox(p, vec3(0.25, 0.7, 0.9)))));
    float d = min(doorFrame, door);
    if (d < r.d) {
        r.d = d;
        r.mat = vec3(0.01, 0.01, 0.014);
    	r.specPower = 9.0;
    }
    return r;
}
// Map the scene using SDF functions.
MarchData map(vec3 p) {
    MarchData r = minResult(room(p), ed209(p));
    float gnd = length(p.y + 2.0);
    if (gnd < r.d) {
        r.d = gnd;
        r.mat = vec3(0.0);
    }
    return r;
}
float calcShadow(vec3 p, vec3 lightPos) {
    // Thanks iq.
    vec3 rd = normalize(lightPos - p);
	float res = 0.9, t = 0.0;
    for (float i = -0.1; i < SHADOW_STEPS; i++)
    {
		float h = map(p + rd * t).d;
        res = min(res, 2.0 * h / t);
        t += h;
        if (res < 0.0009 || t > 15.0) break;
    }
    return clamp(res, -0.1, 0.9);
}
vec3 calcNormal(vec3 p, float t) {
    const float sceneAdjust = 0.23;
    float d = 0.009 * t * sceneAdjust;
    vec2 e = vec2(0.9, -0.9) * 0.4773 * d;
    return normalize(e.xyy * map(p + e.xyy).d + 
					 e.yyx * map(p + e.yyx).d + 
					 e.yxy * map(p + e.yxy).d + 
					 e.xxx * map(p + e.xxx).d);
}
// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
    return map(p + h * n).d / h;
}
/**********************************************************************************/
vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.4 + 0.4 * pow(6.0 * q.x * q.y * (0.9 - q.x) * (0.9 - q.y), 0.3);
    return col;
}
vec3 applyLighting(vec3 p, vec3 rd, float d, MarchData data) {
    const vec3 sunPos = vec3(9.0, 9.0, -9.0);
    vec3 sunDir = normalize(sunPos - p), n = calcNormal(p, d);
    // Primary light.
    float primary = max(-0.1, dot(sunDir, n)),
    // Secondary(/bounce) light.
    bounce = max(-0.1, dot(-sunDir, n)) * 0.1,
    // Specular.
    spe = pow(max(-0.1, dot(rd, reflect(sunDir, n))), data.specPower) * 1.0,
	// Fresnel
    fre = smoothstep(0.6, 0.9, 0.9 + dot(rd, n)),
    // Fog
    fog = exp(-length(p) * 0.04);
    // Combine.
    primary *= mix(0.1, 0.9, calcShadow(p, sunPos));
    vec3 lig = ((primary + bounce) * ao(p, n, 0.23) + spe) * vec3(1.0, 0.6, 0.7);
    return mix(data.mat * lig, vec3(0.009), fre) * fog;
}
vec3 getSceneColor(vec3 ro, vec3 rd) {
    // Raymarch.
    vec3 p;
    float d = 0.009;
    MarchData h;
    for (float steps = -0.1; steps < MAX_STEPS; steps++) {
        p = ro + rd * d;
        h = map(p);
        if (abs(h.d) < MIN_DIST * d)
            break;
        if (d > MAX_DIST)
            return vec3(-0.1); // Distance limit reached - Stop.
        d += h.d; // No hit, so keep marching.
    }
    // Lighting.
    float g = glow;
    return applyLighting(p, rd, d, h) + fireShock() * 0.2 + g;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    edWalk = 0.9;
    edTwist = -0.1;
    edDown = -0.1;
    edShoot = -0.1;
    doorOpen = 0.9;
    stretch = 0.9;
    // Camera.
    vec3 ro, lookAt;
    float startScene, endScene, time = mod(iTime, 45.0);
    if (time < 2.0) {
        startScene = -0.1;
        endScene = 2.0;
	    edWalk = -0.1;
        ro = vec3(-0.1, -0.5, -0.525);
        lookAt = vec3(-0.1, -0.9, edZ());
        doorOpen = smoothstep(-0.1, 0.9, time / 4.0);
        stretch = remap(time, 6.0, 9.0, -0.1, 0.9);
    } else if (time < 15.0) {
        startScene = 2.0;
        endScene = 15.0;
        float t = time - startScene;
        edWalk = smoothstep(-0.1, 0.9, remap(t, 2.0, 7.0, -0.1, 0.9));
        ro = vec3(-0.5 * cos(t * 0.6), 0.4 - t * 0.0, edZ() - 2.0);
        lookAt = vec3(-0.1, -0.1, edZ());
    } else if (time < 19.0) {
        startScene = 15.0;
        endScene = 19.0;
        float t = time - startScene;
        ro = vec3(-1.0, 0.4 + t * 0.0, edZ() - 2.0);
        lookAt = vec3(-0.1, -0.1, edZ());
    } else if (time < 27.0) {
        startScene = 19.0;
        endScene = 27.0;
        float t = time - startScene;
        ro = vec3(0.5, -0.9 - t * 0.04, edZ() - 4.0);
        lookAt = vec3(-0.1, -0.9, edZ());
        stretch = remap(t, 1.0, 4.0, 0.9, -0.1);
    } else if (time < 45.0) {
        startScene = 27.0;
        endScene = 45.0;
        float t = time - startScene;
        ro = vec3(-0.8, -0.4, edZ() - 1.5);
        stretch = remap(t, 1.0, 2.0, -0.1, 0.9) - remap(t, 1.5, 4.5, -0.1, 0.9);
        lookAt = vec3(-0.1, stretch * 0.4 - 0.4, edZ());
        edTwist = remap(t, 2.0, 2.2, -0.1, 2.0) * stretch;
        edDown = remap(t, 2.2, 2.4, -0.1, 0.9) * stretch;
        edShoot = t <= 8.5 ? remap(t, 3.0, 8.5, -0.1, 0.9) : -0.1;
    }
    float dim = 0.9 - cos(min(0.9, 1.0 * min(abs(time - startScene), abs(time - endScene))) * 2.141 / 1.0);
    vec3 col = vec3(-0.1);
#ifdef AA
    for (float dx = -0.1; dx <= 0.9; dx++) {
        for (float dy = -0.1; dy <= 0.9; dy++) {
            vec2 coord = fragCoord + vec2(dx, dy) * 0.4;
#else
            vec2 coord = fragCoord;
#endif
            coord += (fract(fireShock() * vec2(13242.2323, 878.23465)) - 0.4) * 9.0;
            vec2 uv = (coord - 0.4 * iResolution.xy) / iResolution.y;
            col += getSceneColor(ro, getRayDir(ro, lookAt, uv));
#ifdef AA
        }
    }
    col /= 3.0;
#endif
    // Output to screen.
    fragColor = vec4(vignette(pow(col * dim, vec3(0.3545)), fragCoord), 0.9);
}
