#define AA  0   // optional antialiasing

float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrCylDf (vec3 p, float r, float h);
float PrEllipsDf (vec3 p, vec3 r);
float PrEllCylDf (vec3 p, vec2 r, float h);
float SmoothMin (float a, float b, float r);
float SmoothBump (float lo, float hi, float w, float x);
vec3 RotToDirLim (vec3 v1, vec3 v2, vec3 p, float aMax);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);

vec3 ltPos, vuPos, dukPos, eyePos;
float tCur, dstFar, roomRd, roomHt, sclFac;
int idObj;
const int idFrm = 1, idWl = 2, idFlrCl = 3, idTbl = 4, idLeg = 5, idLit = 6,
   idBdy = 7, idBk = 8, idEye = 9;
bool isSh;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float DuckDf (vec3 p, float dMin)
{
  vec3 q;
  vec2 cs, r;
  float d, h, s;
  dMin /= sclFac;
  p /= sclFac;
  cs = sin (0.3 + vec2 (0.5 * pi, 0.));
  h = 0.5;
  r = vec2 (0.2, 0.3 + 0.05 * clamp (p.z, -2., 2.));
  s = (length (p.xz / r) - 1.) * min (r.x, r.y);
  d = min (max (s, abs (p.y) - h), length (vec2 (s, p.y)) - h);
  DMIN (idBdy);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.5, 0.3, 0.6);
  q.yz = Rot2Cs (q.yz, cs);
  q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
  q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
  r = vec2 (0.3, 0.5 + 0.08 * clamp (q.z, -2., 2.));
  h = 0.07;
  s = (length (q.yz / r) - 1.) * min (r.x, r.y);
  d = SmoothMin (min (max (s, abs (q.x) - h), length (vec2 (s, q.x)) - h), dMin, 0.05);
  DMIN (idBdy);
  d = SmoothMin (PrEllipsDf (p - vec3 (0., 0.75, -0.4), vec3 (0.4, 0.35, 0.5)), dMin, 0.1);
  DMIN (idBdy);
  q = p - vec3 (0., 0.65, -0.9);
  q.zy = Rot2Cs (q.zy, vec2 (cs.x, - cs.y));
  h = 0.15;
  r = vec2 (0.15, 0.04) * (1. - 0.1 * min (2., max (0., 1. - q.z / h)));
  d = PrEllCylDf (q, r, h);
  q.z -= -0.9 * h;
  d = SmoothMin (max (d, - PrEllCylDf (q, r - 0.02, 0.5 * h)), dMin, 0.01);
  DMIN (idBk);
  q = p;
  q.x = abs (q.x);
  d = PrSphDf (q - eyePos, 0.125);
  DMIN (idEye);
  return 0.9 * sclFac * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, nw, w;
  dMin = dstFar;
  nw = 9.;
  q = p;
  q.y -= roomHt;
  q.xz = Rot2D (q.xz, 2. * pi * floor (nw * atan (q.z, - q.x) / (2. * pi) + 0.5) / nw);
  q.x += roomRd;
  w = roomRd * tan (pi / nw);
  d = max (q.x, - PrBox2Df (q.yz, vec2 (roomHt - 0.2, w - 0.2)));
  DMIN (idFrm);
  d = q.x + 0.25;
  DMIN (idWl);
  d = roomHt - abs (q.y);
  DMIN (idFlrCl);
  q = p;
  q.y -= 1.;
  d = PrCylDf (q.xzy, 0.6, 0.02);
  DMIN (idTbl);
  q = p;
  q.y -= 0.5;
  q.xz += 0.02 * sin (7. * pi * q.y + vec2 (0.5 * pi, 0.));
  d = PrCylDf (q.xzy, 0.03, 0.5);
  DMIN (idLeg);
  q = p;
  d = PrCylDf (q.xzy, 0.2, 0.04 * (1. - 0.6 * smoothstep (0.1, 0.2, length (q.xz))));
  DMIN (idLeg);
  q = p - dukPos;
  dMin = DuckDf (q, dMin);
  if (! isSh) {
    q = p;
    q -= ltPos;
    d = PrSphDf (q, 0.1);  
    DMIN (idLit);
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd, float ltDist)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    h = ObjDf (p);
    sh = min (sh, smoothstep (0., 0.03 * d, h));
    d += clamp (h, 0.02, 0.2);
    if (sh < 0.05 || d > ltDist) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, refCol, vn, ltVec, ltDir, ve;
  float dstObj, w, sx, ld, sh;
  const int nRef = 5;
  sclFac = 0.4;
  eyePos = vec3 (0.26, 0.825, -0.6);
  col = vec3 (0.);
  refCol = vec3 (1.);
  isSh = false;
  for (int n = VAR_ZERO; n < nRef; n ++) {
    dstObj = ObjRay (ro, rd);
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (n < nRef - 1 && dstObj < dstFar && idObj == idWl) {
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      refCol *= 0.8;
    } else break;
  }
  if (dstObj < dstFar) {
    if (idObj == idBdy) {
      w = -0.6 - 1.2 * dot (rd, vn);
      col4.rgb = mix (HsvToRgb (vec3 (mod (w + 0.3, 1.), 1., 1.)),
         HsvToRgb (vec3 (mod (w - 0.3, 1.), 1., 1.)), 0.5);
      col4 = vec4 (mix (HsvToRgb (vec3 (mod (w, 1.), 1., 1.)), col4.rgb, 0.5), 0.2);
    } else if (idObj == idBk) {
      col = vec3 (0.3, 0.1, 0.);
      col4 = vec4 (col, 0.2);
    } else if (idObj == idEye)  {
      sx = sign (ro.x);
      ve = eyePos * sclFac;
      ve.x *= sx;
      ve = RotToDirLim (normalize (vuPos - dukPos - ve), vec3 (sx, 0., 0.),
         ro - dukPos - ve, 0.35 * pi);
      col4 = vec4 (vec3 (0.5, 1., 0.5) * (1. - 0.9 * step (length (ve.yz) / sclFac, 0.05 +
         0.01 * SmoothBump (0.25, 0.75, 0.25, mod (8. * atan (ve.z, - ve.y) / pi, 1.)))), -2.);
    } else if (idObj == idFrm) {
      col4 = vec4 (0.5, 0.2, 0., 0.1);
    } else if (idObj == idWl) {
      col4 = vec4 (0.1, 0.3, 0., 0.);
    } else if (idObj == idFlrCl) {
      col4 = (ro.y < roomHt) ? vec4 (0.35, 0.3, 0.2, 0.) : vec4 (0.7, 0.7, 1., 0.);
    } else if (idObj == idTbl) {
      col4 = vec4 (0.85, 0.95, 0.9, 0.2);
      if (vn.y > 0.99) col4 = mix (vec4 (0.5, 0.7, 0.4, 0.1), col4, 
         smoothstep (0.015, 0.02, abs (Fbm2 (vec2 (4., 1.) * ro.xz) - 0.4)));
    } else if (idObj == idLeg) {
      col4 = vec4 (0.5, 0.5, 0.55, 0.2);
    } else if (idObj == idLit) {
      col4 = vec4 (1., 1., 0.7, -1.);
    }
    if (idObj == idFlrCl || idObj == idFrm) vn = VaryNf (32. * ro, vn, 0.2);
    if (col4.a >= 0.) {
      isSh = true;
      ltVec = ltPos - ro;
      ld = length (ltVec);
      ltDir = ltVec / ld;
      if (idObj == idBdy) col4.rgb = mix (vec3 (0.5, 0.3, 0.3), col4.rgb,
         smoothstep (-0.2, 0.2, dot (vn, ltDir)));
      sh = ObjSShadow (ro + 0.01 * vn, ltDir, ld);
      col = refCol * (col4.rgb * (0.2 + 0.3 * max (- dot (vn, ltDir), 0.) +
         0.8 * sh * max (dot (vn, ltDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.));
    } else if (col4.a == -1.) col = col4.rgb * (0.6 - 0.4 * dot (rd, vn));
    else if (col4.a == -2.) col = col4.rgb;
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, u, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr, ltAz, ltEl;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.5 * pi;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az -= 2. * pi * sin (0.005 * pi * tCur);
  }
  el = clamp (el, -0.35 * pi, 0.35 * pi);
  roomRd = 6.;
  roomHt = 2.;
  ro = vec3 (0.6 * roomRd * cos (el) * sin (az + vec2 (0.5 * pi, 0.)),
     0.9 * roomHt * sin (el)).xzy;
  ro.y += roomHt;
  dukPos = vec3 (0., 1.21, 0.);
  vd = normalize (dukPos - ro);
  u = - vd.y * vd;
  vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / sqrt (1. - vd.y * vd.y),
     vec3 (u.x, 1. + u.y, u.z) / sqrt (1. - vd.y * vd.y), vd);
  vuPos = ro;
  zmFac = 3.;
  dstFar = 20.;
  ltAz = 0.1 * pi * tCur;
  ltEl = 0.15 * pi * sin (2.3 * ltAz);
  ltPos = vec3 (0.5 * roomRd * cos (ltEl) * sin (ltAz + vec2 (0.5 * pi, 0.)),
     0.8 * roomHt * sin (ltEl)).xzy;
  ltPos.y += 1.4 * roomHt;
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec3 RotToDirLim (vec3 v1, vec3 v2, vec3 p, float aMax)
{
  vec3 n;
  float c;
  n = normalize (cross (v1, v2));
  c = max (dot (v1, v2), cos (aMax));
  return c * p + sqrt (1. - c * c) * cross (n, p) + (1. - c) * dot (n, p) * n;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}
