{
    p = floor(vec2(0.4,0.4)*p);
    if(p.x < N && p.y < N)
    {
    	float id = p.x + p.y*N;
        vec2 i = getID(id);
    	fragColor.xyz = 1.*vec3(1,1,1)*jet_range(getParticle(i.x).z,0.,1.);
    }
}

