void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 distortionOffset = vec2(0.0,0.0);
    vec2  texCoord = uv;
    float time = iTime * 1.5;
    texCoord.x += 0.2 * cos(1.0 * uv.y + time );
    texCoord.y += 0.2 * sin(1.0 * uv.x + time);

    vec4 c = fragColor;

    //根据变化的uv值采样通道0的贴图
	vec4 channel0Color = texture(iChannel0,texCoord); 
    vec3 greyValue = vec3(0.299, 0.587, 0.114);
    float grey = dot(channel0Color.xyz,greyValue);
    vec3 col = vec3(grey,grey,grey);// vec3(0.2126*channel0Color.r , 0.7152*channel0Color.g , 0.0722*channel0Color.b ); 

    // Output to screen
    fragColor = vec4(col,1.0);
}

//绘制红心
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center = iResolution.xy/2.0;
    vec2 p = 2.0 * (fragCoord.xy - center.xy)/iResolution.xy;

    float time = iTime;
    // animate
    float tt = mod(time,2.0)/2.0;
    float ss = pow(tt,.2)*0.5 + 0.5;
    ss -= ss*0.2*sin(tt*6.2831*5.0)*exp(-tt*6.0);
    p *= vec2(0.5,1.5) + ss*vec2(0.5,-0.5);


    float a = atan(p.x,p.y)/3.141593;
    float r = length(p);

    // shape
    float h = abs(a);
    float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

    // color
    float f = step(r,d) * pow(1.0-r/d,0.25);

    // Output to screen
    fragColor = vec4(f,0,0,1.0);
}
