#define PI 3.14159265359

// From https://thebookofshaders.com/12/
vec2 random( vec2 p ) {
      return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

// From https://thebookofshaders.com/edit.php#12/2d-voronoi.frag
vec3 voronoi(in vec2 pos) {
  vec2 n = floor(pos);
  vec2 f = fract(pos);

  vec2 mg, mr;
  float md = 8.0;
  for (int j = -1; j <=1; j++) {
    for (int i = -1; i <= 1; i++) {
      vec2 g = vec2(float(i), float(j));
      vec2 o = random(n + g);
      o = 0.5 + 0.5 * sin(iTime + 6.2831 * o);

      vec2 r = g + o - f;
      float d = dot(r, r);

      if (d < md ) {
        md = d;
        mr = r;
        mg = g;
      }
    }
  }

  return  vec3(mr, mg.x);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 pos = fragCoord / iResolution.xy;

    float y = smoothstep(0.2,0.5,pos.x) - smoothstep(0.5,0.8,pos.x);
    pos.y += random(pos).y;

    vec3 color = vec3(0.0, 0.0, 0.0);

    vec3 c = voronoi(pos);
    color = c.x * (1.5 + 1.0 * sin(164.0 * c.x)) * vec3(1.0);


    fragColor = vec4(1.0, color.x + 0.1, color.y + 0.1, 1.0);
}
