/*Ethan Alexander Shulman 2020 - xaloez.com
4k 60fps video https://www.youtube.com/watch?v=e-35_IJixzg
4k wallpaper xaloez.com/art/2020/MandelSphereFractal.jpg

Controls:
Look - Mouse Drag
Move - WASD or Arrow Keys
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c = texelFetch(iChannel0,ivec2(fragCoord),0);
    fragColor = pow(c/c.w,vec4(1./2.2));
}

/*Buffer A stores dynamic camera position and rotation.

0 = camera position(xyz), w = 1 frame of camera change
1 = camera yaw/pitch(xy) and last mouse position(zw)
*/

#define MOUSE_SENSITIVITY 2.

#define MOVEMENT_SPEED 5.


mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y < 1.) {
        vec4 rot = texelFetch(iChannel0,ivec2(1,0),0);
        float camChanged = (iMouse.z > 0. && iMouse.w < 1.)?
                            (length(rot.zw-iMouse.xy)>0.?1.:0.):
                            0.;

        if (fragCoord.x < 1.) {
            //camera position
            vec3 pos = texelFetch(iChannel0,ivec2(0),0).xyz;
            if (iFrame == 0) pos = vec3(0,7,0);//default position

            #define kb(i) texelFetch(iChannel1,ivec2(i,0),0).x

            //left/right/A/D arrows
            float right = max(kb(39),kb(68))-max(kb(37),kb(65));
            if (right != 0.) {
                vec3 rt = vec3(right*iTimeDelta*MOVEMENT_SPEED,0,0);
                rt.yz *= r2(rot.y);
                rt.xz *= r2(rot.x);
                pos += rt;
                camChanged = 1.;
            }

            //space key
            if (kb(32) > 0.) {
                vec3 up = vec3(0,iTimeDelta*MOVEMENT_SPEED,0);
                up.yz *= r2(rot.y);
                up.xz *= r2(rot.x);
                pos += up;
                camChanged = 1.;
            }

            //up/down/W/S arrows
            float forward = max(kb(38),kb(87))-max(kb(40),kb(83));
            if (forward != 0.) {
                vec3 fwd = vec3(0,0,forward*iTimeDelta*MOVEMENT_SPEED);
                fwd.yz *= r2(rot.y);
                fwd.xz *= r2(rot.x);
                pos += fwd;
                camChanged = 1.;
            }

            fragColor = vec4(pos,camChanged);
        } else {
            //camera rotation
            if (iFrame == 0) rot = vec4(0);

            //rotate on mouse drag
            if (iMouse.z > 0. && iMouse.w < 1.) {
                rot.xy -= (iMouse.xy-rot.zw)/iResolution.y*MOUSE_SENSITIVITY;
            }
            fragColor = vec4(rot.xy,iMouse.xy);
        }
    }
}

/*Ethan Alexander Shulman 2020 - xaloez.com
4k 60fps video https://www.youtube.com/watch?v=e-35_IJixzg
4k wallpaper xaloez.com/art/2020/MandelSphereFractal.jpg

Controls:
Look - Mouse Drag
Move - WASD or Arrow Keys
*/

#define time iTime
#define frame iFrame

#define STEPS 100
#define EPS 4e-2
#define PI 3.14159265

#define FIELD_OF_VIEW 1.

mat2 r2(float a) {
	float s = sin(a), c = cos(a);
	return mat2(c,-s,s,c);
}

mat2x3 tangentBinormal(vec3 norm) {
	mat2x3 t;
	if (abs(norm.x) > abs(norm.y)) t[0] = normalize(vec3(-norm.z,0,norm.x));
	else t[0] = normalize(vec3(0,norm.z,-norm.y)); 
	t[1] = cross(norm,t[0]);
	return t;
}

//Credit: IQ, integer hash 2 https://www.shadertoy.com/view/XlXcW4
vec3 hash(uvec3 x) {
    x = ((x>>8U)^x.yzx)*1103515245U;
    x = ((x>>8U)^x.yzx)*1103515245U;
    x = ((x>>8U)^x.yzx)*1103515245U;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec3 hsvToRGB(vec3 hsv) {
	return mix(clamp(abs(mod(hsv.x*6.0+vec3(0.,4.,2.),6.)-3.0)-1.0,0.,1.),vec3(1),hsv.y)*hsv.z;
}

#define START_OPACITY(posSeed) vec3 opacityRand = hash(uvec3(abs(posSeed)*1000.)+uvec3(frame,frame*3,frame*7))
#define OPACITY(dst,skip,alpha) (dst+skip*opacityRand.y*step(alpha,opacityRand.x))

float geometry(vec3 p) {
	float d = 1e8, sz = 5.;
	for (float i = 1.; i < 8.; i++) {
		d = min(d,length(p)-sz);
		p = abs(abs(p)-sz);
		sz *= .45;
	}
	return d;
}
//mat[0] = color, mat[1].x = roughness, mat[1].y = emission
mat2x3 material(vec3 p) {

	return mat2x3(vec3(.5), vec3(.1,0.,0));
}
vec3 normal(vec3 p) {
	#define NEPS vec3(EPS,0,0)
	vec3 n = normalize(vec3(geometry(p+NEPS),geometry(p+NEPS.yxz),geometry(p+NEPS.yzx))-geometry(p));
	return n!=n?normalize(fract(abs(p.zxy*1e6))-.5):n;
}

vec3 background(vec3 rp, vec3 rd) {
	return vec3(.2,.4,.7)+vec3(1)*max(0.,1.-length(rd-vec3(-1,1,-1)))*200.;
}

float twave(float x) {
	return 1.-abs(fract(x)-.5)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec4 camPos = texelFetch(iChannel1,ivec2(0),0);

	vec3 rp = camPos.xyz,//camera position
		rd = normalize(vec3(uv.xy+(hash(uvec3(fragCoord,frame)).xy*4.-2.)/iResolution.y,FIELD_OF_VIEW)),
		c = vec3(1);
	float l = 0.;

	//camera rotation
    vec2 camRot = texelFetch(iChannel1,ivec2(1,0),0).xy;
	rd.yz *= r2(camRot.y);
   	rd.xz *= r2(camRot.x);


	int i;
	for (i = 0; i < STEPS; i++) {
		float dst = geometry(rp);
		if (dst <= EPS) {
			mat2x3 mat = material(rp);
			l += mat[1].y;
			c *= mat[0];

			rp -= rd*(EPS*2.-dst);
			vec3 nrm = normal(rp);
			mat2x3 tangBinorm = tangentBinormal(nrm);

			vec2 rnd = hash(uvec3(abs(rp)*1000.)+uvec3(frame,frame*3,frame*7)).xy;
			float ct = pow(rnd.x,mat[1].x),
				st = sqrt(1.-ct*ct),
				pt = rnd.y*PI*2.;
			vec3 surf = normalize(nrm*ct+tangBinorm[0]*(st*cos(pt))+tangBinorm[1]*(st*sin(pt)));
			rd = reflect(rd,surf);
			dst = 0.;
		}
		rp += rd*dst;

		if (dot(rp,rp) > 20.*20.) i = STEPS;
	}

	//background + final lighting
	c *= l+background(rp,rd)*(i>=STEPS?1.:clamp(geometry(rp)-5.,0.,1.));

    fragColor = (camPos.w>.5?vec4(0):texelFetch(iChannel0,ivec2(fragCoord),0))+vec4(c,1)*1e-2;
}
