void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 UV = fragCoord.xy / iResolution.xy;

    float XRed = pow(abs((-UV.x + 0.5) * abs(sin(iTime))), 0.5);
    float YRed = pow(abs((-UV.y + 0.5) * abs(sin(iTime))), 0.5);

    float XRedB = pow(abs((-UV.x + 0.25) * abs(sin(iTime))), 0.5);
    float YRedB = pow(abs((-UV.y + 0.25) * abs(sin(iTime))), 0.5);

    float XRedC = pow(abs((-UV.x + 0.75) * abs(sin(iTime))), 0.5);
    float YRedC = pow(abs((-UV.y + 0.75) * abs(sin(iTime))), 0.5);


    float XBlue = pow(abs((-UV.x + 0.5) * abs(sin(iTime * 8.0))), 0.5);
    float YBlue = pow(abs((-UV.y + 0.5) * abs(sin(iTime * 8.0))), 0.5);

    float XBlueB = pow(abs((-UV.x + 0.25) * abs(sin(iTime * 8.0))), 0.5);
    float YBlueB = pow(abs((-UV.y + 0.25) * abs(sin(iTime * 8.0))), 0.5);

    float XBlueC = pow(abs((-UV.x + 0.75) * abs(sin(iTime * 8.0))), 0.25);
    float YBlueC = pow(abs((-UV.y + 0.75) * abs(sin(iTime * 8.0))), 0.25);

    float XGreen = pow(abs((-UV.x + 0.5) * abs(sin(iTime * 16.0))), 0.25);
    float YGreen = pow(abs((-UV.y + 0.5) * abs(sin(iTime * 16.0))), 0.25);

    float XGreenB = pow(abs((-UV.x + 0.25) * abs(sin(iTime * 16.0))), 0.25);
    float YGreenB = pow(abs((-UV.y + 0.25) * abs(sin(iTime * 16.0))), 0.25);

    float XGreenC = pow(abs((-UV.x + 0.75) * abs(sin(iTime * 16.0))), 0.25);
    float YGreenC = pow(abs((-UV.y + 0.75) * abs(sin(iTime * 16.0))), 0.25);

    float FinalRedX = XRed * XRedB * XRedC;
    float FinalRedY = YRed * YRedB * YRedC;

    float FinalBlueX = XBlue * XBlueB * XBlueC;
    float FinalBlueY = YBlue * YBlueB * YBlueC;

    float FinalGreenX = XGreen * XGreenB * XGreenC;
    float FinalGreenY = YGreen * YGreenB * YGreenC;

    float FinalRed = YRed * XRed;
    float FinalBlue = YBlue * XBlue;
    float FinalGreen = YGreen * XGreen;

    float HorizontalOverlay = abs(sin((UV.x * 500.0) + (iTime * 2.0)));
    float VerticalOverlay = abs(sin((UV.y * 400.0) + (iTime * 2.0)));

    vec4 FirstPass = vec4(1.0 - FinalRed, 1.0 - FinalGreen, 1.0 - FinalBlue, 1.0);
    vec4 SecondPass = vec4(HorizontalOverlay, HorizontalOverlay, HorizontalOverlay, 1.0);
    vec4 ThirdPass = vec4(VerticalOverlay, VerticalOverlay, VerticalOverlay, 1.0);
    vec4 FourthPass = vec4(FinalRed, FinalBlue, FinalGreen, 0.60);
    vec4 EirdPass = vec4(VerticalOverlay, VerticalOverlay, VerticalOverlay, 1.0);








    fragColor = FirstPass * SecondPass * ThirdPass;

}
