ivec4 get(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
}

ivec4 getb(int id)
{
    return floatBitsToInt(texel(ch2, i2xy(id)));
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

vec2 Fv(vec4 p0, int pid)
{
    if(pid < 0 || pid >= tot_n) return vec2(0); 
   	vec4 p1 = getParticle(pid);
    vec2 viscosity = 0.0*(p1.zw - p0.zw)*clamp(exp(-0.3*distance(p1.xy,p0.xy)*distance(p1.xy,p0.xy)), 0.05,1.);
    vec2 pressure = normalize(p1.xy-p0.xy)*F(distance(p1.xy,p0.xy));
    return viscosity + pressure;
}


void mainImage( out vec4 fragColor, in vec2 pos )
{
    N = ivec2(size*ss);
    tot_n = N.x*N.y;
    ivec4 nb = get(ivec2(pos));
 	vec4 p0 = getParticle(nb.x);

    ivec4 nbm = get(ivec2(iMouse.xy));
    ivec4 nbb = getb(nbm.x);

    float d = 0.;
    for(int i = 0; i<4; i++)
    {
        vec4 pi = getParticle(nbb[i]);
        d += exp(-distance(pos,pi.xy));
    }

    vec4 d0 = vec4(0.);
    vec2 F = Fv(vec4(pos,0,0), nb.x) +
             Fv(vec4(pos,0,0), nb.y) +
             Fv(vec4(pos,0,0), nb.z) +
             Fv(vec4(pos,0,0), nb.w);
    for(int i = 0; i<4; i++)
    {
        vec4 pi = getParticle(nb[i]);
        d0 += 0.45*cos(2.*vec4(3,2,1,4)*min(log(1.+length(F)),1.))/(1.+0.3*distance(pos,pi.xy));
    }

    fragColor = d0+ 1.5*sin(5.*vec4(4,3,2,1)*d);
}

#define size iResolution.xy
#define pixel(a, p) texture(a, p/vec2(textureSize(a,0)))
#define texel(a, p) texelFetch(a, ivec2(p), 0)
#define ch0 iChannel0
#define ch1 iChannel1
#define ch2 iChannel2
#define ch3 iChannel3
#define PI 3.14159265

#define dt 1.0
//particle array proportion 
#define ss 0.5

#define istr 0.3
#define limstr 1.
#define visc 0.004
#define grav 0.001

float F(float d)
{
    return clamp(istr*(0.2/(1.+0.8*d*d) - 2.*exp(-0.9*d*d)), -limstr, limstr);
}

ivec2 N;
int tot_n;

float hash11(float p)
{
    p = fract(p * 15.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p) - 0.5;
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

const int k = 1664525;  

ivec4 hash( ivec4 x )
{
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    x = ((x>>8)^x.wxyz)*k;
    return ivec4(x);
}

ivec2 i2xy(int id)
{
    return ivec2(id%N.x, id/N.x);
}

int xy2i(ivec2 p)
{
    return p.x + p.y*N.x;
}

ivec2 cross_distribution(int i)
{
    return (1<<(i/4)) * ivec2( ((i&2)/2)^1, (i&2)/2 ) * ( 2*(i%2) - 1 );
}

//particle buffer

int cid;

ivec4 get(int id)
{
    return floatBitsToInt(texel(ch0, i2xy(id)));
}

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

vec2 Fv(vec4 p0, int pid)
{
    if(pid < 0 || pid >= tot_n || pid == cid) return vec2(0); 
   	vec4 p1 = getParticle(pid);
    float d= distance(p0.xy, p1.xy);
    vec2 dv = (p1.zw - p0.zw);
    float dotv = dot(normalize(p1.xy-p0.xy), normalize(dv)); //divergence correction
    vec2 antidivergence = 0.08*dv*abs(dotv)*exp(-0.5*d);
    vec2 viscosity = visc*dv*dot(dv,dv)*exp(-0.1*d);
    vec2 pressure = normalize(p1.xy-p0.xy)*F(d);
    return viscosity + pressure + antidivergence;
}

void mainImage( out vec4 U, in vec2 pos )
{
    ivec2 p = ivec2(pos);
    N = ivec2(size*ss);
    tot_n = N.x*N.y;
    if(p.x < N.x && p.y < N.y)
    {
        pos = floor(pos);
        //this pixel value
        U = texel(ch1, pos);
        int id = xy2i(p);
        cid = id;

        //this pixel value
        if(iFrame<10)
        {
            U.xy = size*hash22(pos);
			U.zw = 2.*(hash22(3.14159*pos) - 0.5);
      		return;
        }

        //neighbors
   		ivec4 cp = get(id);

        vec2 F = Fv(U, cp.x) +
            	 Fv(U, cp.y) +
            	 Fv(U, cp.z) +
                 Fv(U, cp.w) + 
            	 grav*vec2(0.,-1.);

        if(iMouse.z > 0.) 
        {
            float d = distance(iMouse.xy, U.xy);
            F += 0.1*normalize(iMouse.xy - U.xy)/(sqrt(d)+2.);
        }

        U.zw += F*dt;
        U.xy += U.zw*dt;
        U.zw = clamp(U.zw, vec2(-2),vec2(2));

        //border conditions
        if(size.x - U.x < 2.) U.z = -abs(U.z);
        if(U.x < 2.) U.z = abs(U.z);
        if(size.y - U.y < 2.) U.w = -abs(U.w);
        if(U.y < 2.) U.w = abs(U.w);


    }
    else discard;
}

//sorting closest 4 particles in axis directions that make a bounding box
//only in particle space, texture buffer not needed

ivec4 get(int id)
{
    return floatBitsToInt(texel(ch0, i2xy(id)));
}

vec4 save(ivec4 v)
{
