ivec4 u; //ids
vec4 d; //distances
vec2 pos; //this particle position
int tid;

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

//insertion sort
void sort(int utemp)
{
    if(utemp == tid || utemp < 0) return;

   	vec4 part = getParticle(utemp);
    vec2 dx = part.xy - pos;
    float dtemp = length(dx);
    //sorting
    if(dx.x > abs(dx.y))
    {
        if(d.x > dtemp) 
        {
            d.x = dtemp;
        	u.x = utemp;
        }
    }
    else if(dx.x < -abs(dx.y))
    {
        if(d.y > dtemp) 
        {
            d.y = dtemp;
        	u.y = utemp;
        }
    }
    else if(dx.y > abs(dx.x))
    {
        if(d.z > dtemp) 
        {
            d.z = dtemp;
        	u.z = utemp;
        }
    }
    else if(d.w > dtemp) 
    {
        d.w = dtemp;
        u.w = utemp;
    }
}

void sortneighbor(int id)
{
    ivec4 nb = get(id);
    for(int j = 0; j < 4; j++)
    {
        sort(nb[j]);
    }
}

void mainImage( out vec4 U, in vec2 fragCoord )
{  
    ivec2 p = ivec2(fragCoord);
    N = ivec2(size*ss);
    tot_n = N.x*N.y;

    if(p.x > N.x || p.y > N.y) discard;

    int id = xy2i(p);

    u = ivec4(-1); d = vec4(1e10); 

    tid = id;
    pos = getParticle(id).xy;

    sortneighbor(id); 

    for(int i = 0; i < 16; i++)
    {
        sort(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    ivec4 nb = get(id);
    for(int i = 0; i < 4; i++)
    {
        sortneighbor(nb[i]); 
    }

    if( any(lessThan(u, ivec4(-1))) || any(greaterThan(u, ivec4(tot_n))))
    {
        u = ivec4(0);
    }


    U = save(u);
}

//4th order voronoi particle tracking 

ivec4 get(ivec2 p)
{
    return floatBitsToInt(texel(ch0, p));
}

ivec4 getnb(int id)
{
    return floatBitsToInt(texel(ch2, i2xy(id)));
}

vec4 save(ivec4 v)
{
    return intBitsToFloat(v);
}

ivec4 u; //ids
vec4 d; //distances
vec2 pos; //pixel position

vec4 getParticle(int id)
{
    return texel(ch1, i2xy(id));
}

float particleDistance(int id, vec2 p)
{
    return distance(getParticle(id).xy, p);
}

//insertion sort
void sort(int utemp)
{
    if(utemp == -1) return; 
   	float dtemp = particleDistance(utemp, pos);
    //sorting
    if(d.x > dtemp)
    {
        d = vec4(dtemp, d.xyz);
        u = ivec4(utemp, u.xyz);
    }
    else if(d.y > dtemp && dtemp > d.x)
    {
        d.yzw = vec3(dtemp, d.yz);
        u.yzw = ivec3(utemp, u.yz);
    }
    else if(d.z > dtemp && dtemp > d.y)
    {
        d.zw = vec2(dtemp, d.z);
        u.zw = ivec2(utemp, u.z);
    }
    else if(d.w > dtemp && dtemp > d.z)
    {
        d.w = dtemp;
        u.w = utemp;
    }
}

void sortpos(ivec2 p)
{
    ivec4 nb = get(p);
    for(int j = 0; j < 4; j++)
    {
        sort(nb[j]);
    }
}

void mainImage( out vec4 U, in vec2 fragCoord )
{
    pos = fragCoord;
    N = ivec2(size*ss);
    tot_n = N.x*N.y;
    ivec2 p = ivec2(pos);

    u = ivec4(-1); d = vec4(1e10); 

    //jump flood sorting 
    sortpos(p); //resort this position
    for(int i = 0; i < 8; i++)
    {
        sortpos(p+cross_distribution(i)); 
    }

    ivec4 dnb = getnb(u.x);
    for(int i = 0; i < 4; i++)
    {
        sort(dnb[i]); 
    }

    for(int i = 0; i < 2; i++)
    {
        sort(hash(ivec4(p, iFrame, i)).x%tot_n); //random sort    
    }

    if( any(lessThan(u, ivec4(-1))) || any(greaterThan(u, ivec4(tot_n))) )
    {
        u = ivec4(0);
    }

    U = save(u);
}
