void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec3 p;
  vec2 r=iResolution.xy;
  float t=iTime;
  float d,l,b1,b2,R=1.57;

  fragColor=vec4(.1,.3,.6,1.);
  for(int i=0;i<77;i++)
  {
    p=vec3((gl_FragCoord.xy-r*.5)/r.y,1.)*l;
    p.yz*=mat2(cos(-.2+vec4(0,-R,R,0)));
    p+=vec3(0,3.,t);
    p.y=mod(p.y-1.5,6.)-3.;
    p.xz=mod(p.xz,1.)-.5;
    b1=length(p-vec3(sin(t*R*12.)/5.,cos(t*R*12.)/8.-2.82,.0))-.1;
    b2=length(p-vec3(0,-3.1,.0))-.2;
    d=min(b1,b2);
    l+=d;
    if(d<.01||b1+b2<.19){fragColor+=vec4(sin(vec3(R*4./3.,R/3.*8.,0)+p.x+l*9.+t*30.)*2.+2.,1)/(l);break;};
  }
}
