// --- 330 chars

#define R (iResolution.xy)
#define S(v) smoothstep(.7*6./R.y,-.7*6./R.y,v)

float draw(vec2 U) {
    vec2 V = ceil(U), B = mod(V,2.), A;
    U = 3.1*(fract(U)-.5);
    A = abs( U * mat2(cos(iTime+vec4(0,33,11,0)) ) );
    return B.x==B.y 
             ? S( max(A.x,A.y) - 1.5 ) * S( min(A.x,A.y) - .5 )
             : 0.;
}

void mainImage( out vec4 O, vec2 u ) {
    vec2 U = 3.* (u+u-R)/R.y;
    O += draw(U);
    U.x = -U.x;
    O += draw(U);
}

