    diftdo=differenceSDF(diftdo,sdesclerotica);
    diftdo=differenceSDF(diftdo, sdPupila);
    diftdo=differenceSDF(diftdo, sdIris);

    float interAB=intersectSDF(sdIris, sdcabeza);
    float interAC=intersectSDF(sdPupila, sdcabeza);
