// pal by iq
vec3 pal (in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 pal_mandlebrot(float x) {
	return pal( x, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );
}

vec3 mandlebrot (vec2 uv) {
	vec2 c = uv;
    vec2 z = uv;
    int i = 0;
    for(; i < 1000 && dot(z,z) < 32.0 ; ++i) {
    	z = vec2(z.x * z.x - z.y * z.y + c.x, 2.0 * z.x * z.y + c.y);
    }
    if(i == 1000) {
    	return vec3(1.0);
    }
    float smooth_i = float(i) - log2(log2(dot(z,z)));
    float ratio = smooth_i / 1000.0;
    return pal_mandlebrot(ratio * 10.0);
}

vec2 remap (vec2 p) {
	return -1.0 + 2.0 * p;
}

mat2 rotate (float time) {
	float s = sin(time);
    float c = cos(time);
    return mat2(
    	c, -s,
        s, c
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 c = remap(uv) * 2.0;
    c.x *= iResolution.x / iResolution.y;
    float zoom = exp(-iTime);
    vec2 offset = vec2(1.0, 0.0);
    vec2 focus_point = vec2(-0.7463, 0.1102);
    vec2 sub = offset - focus_point;
    float angle = atan(sub.y, sub.x);
    c += offset;
    c *= zoom;
    c *= rotate(angle);
    c += focus_point;
	vec3 col = mandlebrot(c);
    fragColor = vec4(col,1.0);
}
