#define PI 3.14159265358

mat2 r2(float a) {
	float sn = sin(a), cs = cos(a);
	return mat2(cs,-sn,sn,cs);
}

vec4 hash(vec4 a) {
	return fract(abs(sin(a.ywxz*766.345)+cos(normalize(a)*4972.92855))*2048.97435+abs(a.wxyz)*.2735);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#define time iTime
    vec2 uv =(fragCoord*2.-iResolution.xy)/iResolution.y;
	float s = 0.;
	for (float l = 1.; l < 20.; l++) {
		vec4 lh = hash(l*vec4(.01,.1,1.,10.));
		vec2 ruv = mod(abs((uv.xy-(lh.xy*2.-1.))*r2(l*l*time*.001+length(uv.xy))),.4)-.2,
			auv = abs(ruv)-.2;
		s += pow(max(-auv.x,-auv.y)*1.5,4.);
	}
	fragColor = vec4(mix(vec3(.03,.037,.036),vec3(.8,.9,1.),min(1.,s*12.)),1);
}
