#define PI 3.1415
#define TWO_PI 6.2831

float sdCircle( vec2 p, float r ){
    return length(p) - r;
}
float sdLine (in vec2 p, in vec2 a, in vec2 b){
	vec2 pa = p-a, ba = b-a;
	float h = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
	return length (pa-ba*h);
}
vec2 ref (vec2 p, vec2 v){   //reflection
    return p - v * (1.*dot(p,v));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	uv *= 2.;      //zoom
    float di = 3.; //reflections number
    vec2 v;
    vec2 rf;

    //circle center
    vec2 p = vec2(sin(iTime*5.),cos(iTime*4.));

    //draw circle
    vec3 col = 1.- vec3(vec2(smoothstep(sdCircle(uv-p ,0.1),0.0,0.01)),1.); 

    for(float i; i<di; i++){
    	v = 1.5*vec2(sin(PI/di*i),cos(PI/di*i));//vector
        rf  = ref(p,v );						//reflection
        //draw
        col +=1.- vec3(smoothstep(sdCircle(uv-rf,0.1),0.0,0.01));
    	col += vec3(step(sdLine(uv,v ,-v ),.01));

    }
    fragColor = vec4(col,1.0);
}
