float horseDist(const vec2 p, vec2 feet){
    float t = iTime*17.;

    vec2 uv = translate(p, feet);
    uv *= 2.;
    vec2 body = vec2(0., 100.);
    vec2 behind = body + vec2(-100., 20.);
    vec2 front = body + vec2(100., 0.);

    front += 20.*sin(t);
    behind += 30.*cos(t)*vec2(0.2, 1.3);

    vec2 tail_start = behind+vec2(-70., 5.);
    vec2 tail_end = tail_start + vec2(-70., 35.*cos(t*1.2));
    vec2 tail_mid = (tail_start+tail_end)/2. + vec2(0.,1.)*50.*sin(t*1.2);

    vec2 backleg_top = behind;
    vec2 backleg_mid = backleg_top+vec2(50.*cos(-t), -150.);
    vec2 backleg_bottom = backleg_mid + vec2(50.*sin(t), -70.*sin(t));

    float ft = t+1.9;
    vec2 frontleg_top = front;
    vec2 frontleg_mid = frontleg_top+vec2(50.*cos(-ft), -150.);
    vec2 frontleg_bottom = frontleg_mid + vec2(50.*sin(ft), -70.*sin(t));

    vec2 head = front+vec2(100., 70.);
    vec2 nose = head+vec2(100., -50.);

    head.y += 15.*sin(t*1.3-1.5);
    nose += 30. * sin(t*1.1);

    vec2 eye_p = head;
    vec2 ears = head+vec2(-20., 30.);
    vec2 ears_end = head+vec2(-70., 50.)+sin(t*1.1)*10.*vec2(0., 1.);

    eye_p += sin(t)* vec2(-5., -5.);

    float d1 = sdCircle(uv, behind, 70.);
    float d2 = sdCircle(uv, front, 50.);
    float d3 = sdBox(uv,body, vec2(50., 50.));

    float tail = sdBezier(uv, tail_start, tail_mid, tail_end)-5.;

    float backleg = sdBezier(uv, backleg_top, backleg_mid, backleg_bottom)-15.;
    float frontleg = sdBezier(uv, frontleg_top, frontleg_mid, frontleg_bottom)-15.;

    float neck = sdLine(uv, front, head)-20.;
    float nasal = sdLine(uv, head, nose)-15.;
    float eye = sdCircle(uv, eye_p, 10.);

    float ear = sdLine(uv, ears, ears_end)-4.;

    float d = smin(d1, d2, 50.);
    d = smin(d, d3, 100.);
    d = smin(d, backleg, 30.);
    d = smin(d, frontleg, 30.);
    d = smin(d, neck, 70.);
    d = smin(d, nasal, 100.);
    d = max(d, -eye);
    d = smin(d, tail, 40.);
    d = smin(d, ear, 30.);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 uv )
{   
    uv /= iResolution.y;
    uv = uv*2.-1.;
    uv = uv*500.;

    uv = rotate(uv, sin(iTime)*0.06);
    uv *= 1.+sin(iTime*9.)*0.05;

    float d;

    // calculate backdrop color
    float d_backdrop = uv.y+50.
        + 30.*sin(uv.x/300. + iTime*1.)
        + 1.*sin(uv.x/50.  + iTime*1.)
        + 100.*sin(iTime);
    d_backdrop = 1.-smoothstep(0.1, 0.5, d_backdrop);
    vec3 c_backdrop = hueShift(vec3(0.1, 0.1, 0.05), iTime)*d_backdrop;

    // calculate floor color
    float d_floor = uv.y+200.
        + 50.*sin(uv.x/300. + iTime*9.)
        + 5.*sin(uv.x/50.  + iTime*9.);
    d_floor = 1.-step(0.1, d_floor);
    vec3 c_floor = hueShift(vec3(0.2, 0.2, 0.1), iTime)*d_floor;

    // calculate horse color
    float d_horse = horseDist(uv, vec2(sin(iTime)*100., -300.));
    d_horse = 1.-step(0.1, d_horse);
    vec3 c_horse = vec3(1., 1., 0.)*d_horse;

    float ct = iTime*0.5;
    vec3 col = vec3(0.);
    // additive color blending / layering
    if(d_horse>0.)
        col += c_horse;
    else if (d_floor > 0.)
    	col += c_floor;
    else if (d_backdrop > 0.)
        col += c_backdrop;
    else {
        col += hueShift(vec3(1., 0., 0.),iTime*0.1);
    }

    fragColor = vec4(col,1.0);
}

vec3 hueShift( vec3 color, float hueAdjust ){

    const vec3  kRGBToYPrime = vec3 (0.299, 0.587, 0.114);
    const vec3  kRGBToI      = vec3 (0.596, -0.275, -0.321);
    const vec3  kRGBToQ      = vec3 (0.212, -0.523, 0.311);

    const vec3  kYIQToR     = vec3 (1.0, 0.956, 0.621);
    const vec3  kYIQToG     = vec3 (1.0, -0.272, -0.647);
    const vec3  kYIQToB     = vec3 (1.0, -1.107, 1.704);

    float   YPrime  = dot (color, kRGBToYPrime);
    float   I       = dot (color, kRGBToI);
    float   Q       = dot (color, kRGBToQ);
    float   hue     = atan (Q, I);
    float   chroma  = sqrt (I * I + Q * Q);

    hue += hueAdjust;

    Q = chroma * sin (hue);
    I = chroma * cos (hue);

    vec3    yIQ   = vec3 (YPrime, I, Q);

    return vec3( dot (yIQ, kYIQToR), dot (yIQ, kYIQToG), dot (yIQ, kYIQToB) );

}

float sdBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      
    float res = 0.0;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;
    if( h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = uv.x + uv.y - kx;
        t = clamp( t, 0.0, 1.0 );
        vec2 qos = d + (c + b*t)*t;
        res = dot(qos,qos);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        t = clamp( t, 0.0, 1.0 );
        vec2 qos = d + (c + b*t.x)*t.x;
        res = dot(qos,qos);
        qos = d + (c + b*t.y)*t.y;
        res = min(res,dot(qos,qos));
        qos = d + (c + b*t.z)*t.z;
        res = min(res,dot(qos,qos));
    }
    return sqrt( res );
}

float opUnion(float d1, float d2) {
    return min(d1, d2);
}

float sdCircle(vec2 p, vec2 c, float r) {
    return length(p-c)-r;
}

float sdBox( vec2 p, vec2 c, vec2 b )
{
    p -= c;
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

float sdLine( vec2 p, vec2 a, vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sdParabola( in vec2 pos, in float k )
{    
    pos.x = abs(pos.x);

    float p = (1.0-2.0*k*pos.y)/(6.0*k*k);
    float q = -abs(pos.x)/(4.0*k*k);

    float h = q*q + p*p*p;
    float r = sqrt(abs(h));

    float x = (h>0.0) ? 
        pow(-q+r,1.0/3.0) - pow(abs(-q-r),1.0/3.0)*sign(q+r) :
        2.0*cos(atan(r,-q)/3.0)*sqrt(-p);

    return length(pos-vec2(x,k*x*x)) * sign(pos.x-x);
}

vec2 translate(vec2 p, vec2 t) {
    return p-t;
}

vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
