#define time iTime*0.1
const int COUNT = 10;
const float SCALE = 32.;

float rand(float x)
{
	return fract(sin(x)*1276818.27865);
}

float rand2(vec2 x)
{
	return fract(sin(dot(x ,vec2(12.9898,78.233))) * 43758.5453);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.x;


    vec3 col = vec3(0.2);
    int l = 0;
    float r = 0.;
    for(int i = 0; i < COUNT; i++)
    {
        vec2 coord = uv*SCALE/float(i);
        coord.x += time*float(i);
		int l2 = max(l, int(step(rand(floor(coord.x))*uv.y*2.*float(i), 0.1))*i);
        if(l2 != l)
        {
			r = clamp(rand2(floor(coord*vec2(8, 32)))-0.8, 0., 1.)*4.;
        }


        l = l2;


    }
    col = vec3(l)/float(COUNT);

    float sun = clamp(length((uv-vec2(0.5, 0.1))*vec2(0.5, 1.))*1.3, 0., 1.);
    vec3 sky = mix(vec3(.6, 0.3, 0.4)*3., vec3(0.1, 0.1, 0.4), sun)*0.7;
    col = vec3(sky);

    if(l > 0)
    {
		col = vec3(0.3);
        col = mix(col, vec3(0.8, 0.8, 0.2)*2., r);
        col = mix(sky, col, float(l)/float(COUNT)*0.5);
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}
