{
	//vec2 st = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    vec2 M = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    float md = distance(uv,M);
    float scale = 30.;
    uv *= scale;
    //uv = vec2(length(uv), atan(uv.y,uv.x)/(2.*3.1415)*2.);

    vec2 gv = fract(uv);
    vec2 id = floor(uv);
    vec2 off = vec2(.5);

    float rid = rand(id);
    float tt = sin(iTime*2.+uv.y*.2+rid*3.);


    float tch = tt*md*3.;
    tch = clamp(tch, 0., 1.);

    float d = distance(uv,id+off)*2.; 

	float c = smoothstep(tch, tch*.5, d);

    fragColor = vec4(c);
}
