// "Marble Paths" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

/*
  Balls in channels formed by random rectangular tiling (mouseable, click
  upper-right for new run, lower-right to switch between distant viewpoint and
  panoramic view near random ball) In addition to gravity and friction there
  is a horizontal force whose direction varies with time.
  Based on "Channeling Marbles", "Truchet Balls" and "Random Rectangular Tiling".
*/

mat3 QtToRMat (vec4 q);
vec2 Rot2D (vec2 q, float a);
vec3 HsvToRgb (vec3 c);
float Hashfv2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

#define VAR_ZERO min (iFrame, 0)

const int nBall = 64;
vec3 vnObj, ltDir;
float tCur, dstFar, surfHt, surfScl;
int idBall;
const float pi = 3.14159;

float RandRect (vec2 p)
{
  vec4 hm, hc;
  vec3 e;
  vec2 pMid[4], pEdge[4], ip;
  float dMin, hp, eFac;
  ip = floor (p) + 0.5;
  e = vec3 (-1., 0., 1.);
  hp = Hashfv2 (ip);
  hm = vec4 (Hashfv2 (ip + e.zy), Hashfv2 (ip + e.xy), Hashfv2 (ip + e.yz), Hashfv2 (ip + e.yx));
  hc = vec4 (Hashfv2 (ip + e.zz), Hashfv2 (ip + e.xx), Hashfv2 (ip + e.xz), Hashfv2 (ip + e.zx));
  if (mod (ip.x + ip.y, 2.) < 0.5) {
    pEdge[0] = vec2 (hm.z - hm.y, hc.z - hp);
    pEdge[1] = vec2 (hm.x - hm.z, hc.x - hp);
    pEdge[2] = vec2 (hm.x - hm.w, hp - hc.w);
    pEdge[3] = vec2 (hm.w - hm.y, hp - hc.y);
    pMid[0] = vec2 (hm.z, hp);
    pMid[1] = pMid[0];
    pMid[2] = vec2 (hm.w, hp);
    pMid[3] = pMid[2];
  } else {
    pEdge[0] = vec2 (hp - hc.z, hm.z - hm.y);
    pEdge[1] = vec2 (hc.x - hp, hm.z - hm.x);
    pEdge[2] = vec2 (hc.w - hp, hm.x - hm.w);
    pEdge[3] = vec2 (hp - hc.y, hm.y - hm.w);
    pMid[0] = vec2 (hp, hm.y);
    pMid[1] = vec2 (hp, hm.x);
    pMid[2] = pMid[1];
    pMid[3] = pMid[0];
  }
  eFac = 0.3;
  for (int k = 0; k < 4; k ++) {
    pEdge[k] = eFac * pEdge[k] + 0.5;
    pMid[k] = 2. * eFac * (pMid[k] - 0.5);
  }
  pMid[0] += pEdge[0] * e.xz;
  pMid[1] += pEdge[1] * e.zz;
  pMid[2] += pEdge[2] * e.zx;
  pMid[3] += pEdge[3] * e.xx;
  dMin = 1e6;
  for (int k = 0; k < 4; k ++) 
     dMin = min (dMin, length (max (abs (p - ip - pMid[k]) - pEdge[k] + eFac - 0.05, 0.)) - 0.01);
  return dMin;
}

float SurfHt (vec2 p)
{
  return surfHt * (1. - smoothstep (0., 0.2, RandRect (surfScl * p)));
}

float SurfRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  dHit = dstFar;
  if (rd.y < 0.) {
    s = - (ro.y - surfHt) / rd.y;
    sLo = s;
    for (int j = VAR_ZERO; j < 160; j ++) {
      p = ro + s * rd;
      h = p.y - SurfHt (p.xz);
      if (h < 0.) break;
      sLo = s;
      s += max (0.2, 0.4 * h);
      if (s > dstFar) break;
    }
    if (h < 0.) {
      sHi = s;
      for (int j = VAR_ZERO; j < 5; j ++) {
        s = 0.5 * (sLo + sHi);
        p = ro + s * rd;
        if (p.y > SurfHt (p.xz)) sLo = s;
        else sHi = s;
      }
      dHit = 0.5 * (sLo + sHi);
    }
  }
  return dHit;
}

vec3 SurfNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnObj = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
}

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 rs, u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    u = ro - p.xyz;
    rad = 0.45;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w >= 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return 0.5 + 0.5 * step (0., sign (a.x) * sign (a.y));
}

vec3 BgCol (vec3 rd)
{
  return vec3 (0.5, 0.7, 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, vnn, bgCol;
  float dstObj, dstSurf, sh;
  bgCol = BgCol (rd);
  dstObj = BallHit (ro, rd);
  dstSurf = SurfRay (ro, rd);
  if (min (dstObj, dstSurf) < dstFar) {
    if (dstObj < dstSurf) {
      ro += dstObj * rd;
      vn = vnObj;
      col4 = vec4 (HsvToRgb (vec3 (float (idBall) / float (nBall), 0.8, BallChqr (idBall, vn))), 0.3);
    } else if (dstSurf < dstFar) {
      ro += dstSurf * rd;
      vn = SurfNf (ro);
      vnn = vn;
      vn = VaryNf (4. * ro, vn, max (0.1, 4. - 4. * dstSurf / dstFar));
      col = mix (vec3 (0.6, 0.55, 0.55), vec3 (0.55, 0.6, 0.55), smoothstep (0., 0.05, ro.y));
      col4 = mix (vec4 (col, 0.), vec4 (0.8, 0.8, 0.9, 0.2), step (0.995, dot (vn, vnn)));
      col = col4.rgb * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    }
    sh = BallHitSh (ro + 0.01 * ltDir, ltDir, 10.);
    col = col4.rgb * (0.2 + 0.1 * max (- dot (vn.xz, normalize (ltDir.xz)), 0.) + 
       0.1 * max (vn.y, 0.) + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
    col = mix (col, bgCol, 1. - min (1., exp2 (4. * (1. - 1.6 * min (dstObj, dstSurf) / dstFar))));

  } else col = bgCol;
  return clamp (col, 0., 1.);
}

#define AA    1

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro, vd, bMid;
  vec2 canvas, uv, uvv, ut;
  float az, el, asp, zmFac, sr, s, mb, bMid2;
  int fBall;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (4 * nBall + 0);
  surfHt = stDat.x;
  surfScl = stDat.y;
  stDat = Loadv4 (4 * nBall + 1);
  mPtr.xyz = stDat.xyz;
  fBall = int (stDat.w);
  az = 0.;
  el = 0.;
  ut = vec2 (mPtr.x, abs (mPtr.y)) + 0.05 * vec2 (1. / asp, 1.) - 0.5;
  mb = min (ut.x, ut.y);
  if (mPtr.z > 0. && mb < 0.) {
    az = 2. * pi * mPtr.x;
    el = 0.5 * pi * (mPtr.y + 0.25);
  } else {
    if (fBall < 0) {
      az += 0.03 * pi * tCur;
      el += pi * (0.17 + 0.1 * sin (0.041 * pi * tCur));
    }
  }
  if (fBall < 0) {
    el = clamp (el, 0.065 * pi, 0.4 * pi);
    stDat = Loadv4 (4 * nBall + 2);
    bMid = stDat.xyz;
    bMid2 = stDat.w;
    ro = bMid + min (2.5 * bMid2, 80.) * vec3 (cos (el) * sin (az + vec2 (0.5 * pi, 0.)), 2. * sin (el)).xzy;
    vd = normalize (bMid - ro);
    s = sqrt (max (1. - vd.y * vd.y, 1e-6));
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x) / s, vec3 (- vd.y * vd.x, 1. - vd.y * vd.y,
       - vd.y * vd.z) / s, vd);
    zmFac = 4.;
  } else {
    ro = Loadv4 (4 * fBall + 0).xyz;
    ro.y += 1.;
    vd = Loadv4 (4 * fBall + 1).xyz;
    vd = normalize (vec3 (vd.x, 0., vd.z));
    vd.xz = Rot2D (vd.xz,  - az);
    ro.xz -= 0.3 * vd.xz;
    vuMat = mat3 (vec3 (vd.z, 0., - vd.x), vec3 (0., 1., 0.), vd);
  }
  dstFar = 200.;
  ltDir = vuMat * normalize (vec3 (1., 2., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = (fBall < 0) ? vec3 (uvv, zmFac) : vec3 (0.5 * sin (uvv.x * pi / asp + vec2 (0., 0.5 * pi)), uvv.y).xzy;
    rd = vuMat * normalize (rd);
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && min (uv.x - asp, abs (uv.y) - 1.) > -0.07)
     col = mix (col, vec3 (1., 1., 0.), 0.5);
  fragColor = vec4 (col, 1.);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e;
  e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Marble Paths" by dr2 - 2020
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

vec4 QtMul (vec4 q1, vec4 q2);
vec4 RMatToQt (mat3 m);
mat3 LpStepMat (vec3 a);
vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
float Hashfv2 (vec2 p);
vec3 Hashv3f (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int nBall = 64;
float todCur, nStep, surfHt, surfScl;
int fBall;
const float pi = 3.14159;
const float txRow = 128.;

#define VAR_ZERO min (iFrame, 0)

float RandRect (vec2 p)
{
  vec4 hm, hc;
  vec3 e;
  vec2 pMid[4], pEdge[4], ip;
  float dMin, hp, eFac;
  ip = floor (p) + 0.5;
  e = vec3 (-1., 0., 1.);
  hp = Hashfv2 (ip);
  hm = vec4 (Hashfv2 (ip + e.zy), Hashfv2 (ip + e.xy), Hashfv2 (ip + e.yz), Hashfv2 (ip + e.yx));
  hc = vec4 (Hashfv2 (ip + e.zz), Hashfv2 (ip + e.xx), Hashfv2 (ip + e.xz), Hashfv2 (ip + e.zx));
  if (mod (ip.x + ip.y, 2.) < 0.5) {
    pEdge[0] = vec2 (hm.z - hm.y, hc.z - hp);
    pEdge[1] = vec2 (hm.x - hm.z, hc.x - hp);
    pEdge[2] = vec2 (hm.x - hm.w, hp - hc.w);
    pEdge[3] = vec2 (hm.w - hm.y, hp - hc.y);
    pMid[0] = vec2 (hm.z, hp);
    pMid[1] = pMid[0];
    pMid[2] = vec2 (hm.w, hp);
    pMid[3] = pMid[2];
  } else {
    pEdge[0] = vec2 (hp - hc.z, hm.z - hm.y);
    pEdge[1] = vec2 (hc.x - hp, hm.z - hm.x);
    pEdge[2] = vec2 (hc.w - hp, hm.x - hm.w);
    pEdge[3] = vec2 (hp - hc.y, hm.y - hm.w);
    pMid[0] = vec2 (hp, hm.y);
    pMid[1] = vec2 (hp, hm.x);
    pMid[2] = pMid[1];
    pMid[3] = pMid[0];
  }
  eFac = 0.3;
  for (int k = 0; k < 4; k ++) {
    pEdge[k] = eFac * pEdge[k] + 0.5;
    pMid[k] = 2. * eFac * (pMid[k] - 0.5);
  }
  pMid[0] += pEdge[0] * e.xz;
  pMid[1] += pEdge[1] * e.zz;
  pMid[2] += pEdge[2] * e.zx;
  pMid[3] += pEdge[3] * e.xx;
  dMin = 1e6;
  for (int k = 0; k < 4; k ++) 
     dMin = min (dMin, length (max (abs (p - ip - pMid[k]) - pEdge[k] + eFac - 0.05, 0.)) - 0.01);
  return dMin;
}

float SurfHt (vec2 p)
{
  return surfHt * (1. - smoothstep (0., 0.2, RandRect (surfScl * p)));
}

vec3 SurfNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec4 p;
  vec3 rmN, rmN1, rmN2, vmN, wmN, dr, dr1, dr2, dv, drw, am, wam;
  float fOvlap, fricN, fricT, fricS, fricSW, fDamp, fPull, grav, rSep,
     fc, ft, drv, dt;
  fOvlap = 500.;
  fricN = 10.;
  fricS = 0.1;
  fricSW = 1.;
  fricT = 0.5;
  fPull = 0.5;
  fDamp = 0.5;
  grav = 10.;
  p = Loadv4 (4 * mId);
  rm = p.xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    rmN = p.xyz;
    dr = rm - rmN;
    rSep = length (dr);
    if (n != mId && rSep < 1.) {
      fc = fOvlap * (1. / rSep - 1.);
      vmN = Loadv4 (4 * n + 1).xyz;
      wmN = Loadv4 (4 * n + 3).xyz;
      dv = vm - vmN;
      drv = dot (dr, dv) / (rSep * rSep);
      fc = max (fc - fricN * drv, 0.);
      am += fc * dr;
      dv -= drv * dr + cross (0.5 * (wm + wmN), dr);
      ft = min (fricT, fricS * abs (fc) * rSep / max (0.001, length (dv)));
      am -= ft * dv;
      wam += (ft / rSep) * cross (dr, dv);
    }
  }
  dr.xz = 0.5 * SurfNf (rm).xz;
  dr.y = rm.y + 0.5 - SurfHt (rm.xz - dr.xz);
  rSep = length (dr);
  if (rSep < 1.) {
    fc = fOvlap * (1. / rSep - 1.);
    dv = vm;
    drv = dot (dr, dv) / (rSep * rSep);
    fc = max (fc - fricN * drv, 0.);
    am += fc * dr;
    dv -= drv * dr + cross (wm, dr);
    ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
    am -= ft * dv;
    wam += (ft / rSep) * cross (dr, dv);
  }
  am += vec3 (Rot2D (vec2 (fPull, 0.), pi * (0.25 + 0.1 * sin (0.001 * nStep))),
     - grav).xzy - fDamp * vec3 (1., 5., 1.) * vm;
  dt = (fBall < 0) ? 0.02 : 0.005;
  vm += dt * am ;
  rm += dt * vm;
  wm += dt * wam / 0.1;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  rm.xz = 0.7 * mIdf * vec2 (1., -1.) + 35. * mod (todCur, 10.);
  rm.y = surfHt + 1.5;
  vm = 0.5 * (2. * Hashv3f (mIdf + todCur) - 1.);
  qm = vec4 (0., 0., 0., 1.);
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qm;
  vec3 rm, vm, wm, bMid;
  vec2 iFrag, canvas, b;
  float asp, bMid2;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 3) discard;
  canvas = iResolution.xy;
  todCur = iDate.w;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  surfHt = 0.8;
  surfScl = 0.25;
  mId = (pxId < 4 * nBall) ? pxId / 4 : -1;
  fBall = -1;
  if (iFrame <= 5) {
    doInit = true;
  } else {
    doInit = false;
    stDat = Loadv4 (4 * nBall);
    nStep = stDat.z;
    ++ nStep;
    stDat = Loadv4 (4 * nBall + 1);
    mPtrP.z = stDat.z;
    fBall = int (stDat.w);
    if (mPtr.z > 0. && mPtrP.z < 0. && mPtr.x > 0.5 - 0.05 / asp && abs (mPtr.y) > 0.5 - 0.05) {
      if (mPtr.y > 0.) {
        if (nStep > 100.) doInit = true;
      } else {
        if (fBall >= 0) fBall = -1;
        else fBall = int (float (nBall) * Hashff (mod (todCur, 1000.)));
      }
    }
  }
  if (doInit) {
    nStep = 0.;
    fBall = -1;
  }
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm);
    else Step (mId, rm, vm, qm, wm);
  }
  if (pxId == 4 * nBall + 2) {
    bMid = vec3 (0.);
    bMid2 = 0.;
    if (nStep > 0.) {
      for (int n = VAR_ZERO; n < nBall; n ++) {
        b = Loadv4 (4 * n).xz;
        bMid.xz += b;
        bMid2 += dot (b, b);
      }
      bMid.xz /= float (nBall);
      bMid.y = 0.1;
      bMid2 = sqrt (bMid2 / float (nBall) - dot (bMid.xz, bMid.xz));
    }
  }
  if (pxId < 4 * nBall) {
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else {
    kp = 4 * nBall;
    if (pxId == kp) stDat = vec4 (surfHt, surfScl, nStep, 0.);
    else if (pxId == kp + 1) stDat = vec4 (mPtr.xyz, float (fBall));
    else if (pxId == kp + 2) stDat = vec4 (bMid, bMid2);
