const float pi = 3.14159;
const float txRow = 128.;

#define VAR_ZERO min (iFrame, 0)

float RandRect (vec2 p)
{
  vec4 hm, hc;
  vec3 e;
  vec2 pMid[4], pEdge[4], ip;
  float dMin, hp, eFac;
  ip = floor (p) + 0.5;
  e = vec3 (-1., 0., 1.);
  hp = Hashfv2 (ip);
  hm = vec4 (Hashfv2 (ip + e.zy), Hashfv2 (ip + e.xy), Hashfv2 (ip + e.yz), Hashfv2 (ip + e.yx));
  hc = vec4 (Hashfv2 (ip + e.zz), Hashfv2 (ip + e.xx), Hashfv2 (ip + e.xz), Hashfv2 (ip + e.zx));
  if (mod (ip.x + ip.y, 2.) < 0.5) {
    pEdge[0] = vec2 (hm.z - hm.y, hc.z - hp);
    pEdge[1] = vec2 (hm.x - hm.z, hc.x - hp);
    pEdge[2] = vec2 (hm.x - hm.w, hp - hc.w);
    pEdge[3] = vec2 (hm.w - hm.y, hp - hc.y);
    pMid[0] = vec2 (hm.z, hp);
    pMid[1] = pMid[0];
    pMid[2] = vec2 (hm.w, hp);
    pMid[3] = pMid[2];
  } else {
    pEdge[0] = vec2 (hp - hc.z, hm.z - hm.y);
    pEdge[1] = vec2 (hc.x - hp, hm.z - hm.x);
    pEdge[2] = vec2 (hc.w - hp, hm.x - hm.w);
    pEdge[3] = vec2 (hp - hc.y, hm.y - hm.w);
    pMid[0] = vec2 (hp, hm.y);
    pMid[1] = vec2 (hp, hm.x);
    pMid[2] = pMid[1];
    pMid[3] = pMid[0];
  }
  eFac = 0.3;
  for (int k = 0; k < 4; k ++) {
    pEdge[k] = eFac * pEdge[k] + 0.5;
    pMid[k] = 2. * eFac * (pMid[k] - 0.5);
  }
  pMid[0] += pEdge[0] * e.xz;
  pMid[1] += pEdge[1] * e.zz;
  pMid[2] += pEdge[2] * e.zx;
  pMid[3] += pEdge[3] * e.xx;
  dMin = 1e6;
  for (int k = 0; k < 4; k ++) 
     dMin = min (dMin, length (max (abs (p - ip - pMid[k]) - pEdge[k] + eFac - 0.05, 0.)) - 0.01);
  return dMin;
}

float SurfHt (vec2 p)
{
  return surfHt * (1. - smoothstep (0., 0.2, RandRect (surfScl * p)));
}

vec3 SurfNf (vec3 p)
{
  vec2 e;
  e = vec2 (0.01, 0.);
  return normalize (vec3 (SurfHt (p.xz) - vec2 (SurfHt (p.xz + e.xy), SurfHt (p.xz + e.yx)), e.x).xzy);
}

void Step (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec4 p;
  vec3 rmN, rmN1, rmN2, vmN, wmN, dr, dr1, dr2, dv, drw, am, wam;
  float fOvlap, fricN, fricT, fricS, fricSW, fDamp, fPull, grav, rSep,
     fc, ft, drv, dt;
  fOvlap = 500.;
  fricN = 10.;
  fricS = 0.1;
  fricSW = 1.;
  fricT = 0.5;
  fPull = 0.5;
  fDamp = 0.5;
  grav = 10.;
  p = Loadv4 (4 * mId);
  rm = p.xyz;
  vm = Loadv4 (4 * mId + 1).xyz;
  qm = Loadv4 (4 * mId + 2);
  wm = Loadv4 (4 * mId + 3).xyz;
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (4 * n);
    rmN = p.xyz;
    dr = rm - rmN;
    rSep = length (dr);
    if (n != mId && rSep < 1.) {
      fc = fOvlap * (1. / rSep - 1.);
      vmN = Loadv4 (4 * n + 1).xyz;
      wmN = Loadv4 (4 * n + 3).xyz;
      dv = vm - vmN;
      drv = dot (dr, dv) / (rSep * rSep);
      fc = max (fc - fricN * drv, 0.);
      am += fc * dr;
      dv -= drv * dr + cross (0.5 * (wm + wmN), dr);
      ft = min (fricT, fricS * abs (fc) * rSep / max (0.001, length (dv)));
      am -= ft * dv;
      wam += (ft / rSep) * cross (dr, dv);
    }
  }
  dr.xz = 0.5 * SurfNf (rm).xz;
  dr.y = rm.y + 0.5 - SurfHt (rm.xz - dr.xz);
  rSep = length (dr);
  if (rSep < 1.) {
    fc = fOvlap * (1. / rSep - 1.);
    dv = vm;
    drv = dot (dr, dv) / (rSep * rSep);
    fc = max (fc - fricN * drv, 0.);
    am += fc * dr;
    dv -= drv * dr + cross (wm, dr);
    ft = min (fricT, fricSW * abs (fc) * rSep / max (0.001, length (dv)));
    am -= ft * dv;
    wam += (ft / rSep) * cross (dr, dv);
  }
  am += vec3 (Rot2D (vec2 (fPull, 0.), pi * (0.25 + 0.1 * sin (0.001 * nStep))),
     - grav).xzy - fDamp * vec3 (1., 5., 1.) * vm;
  dt = (fBall < 0) ? 0.02 : 0.005;
  vm += dt * am ;
  rm += dt * vm;
  wm += dt * wam / 0.1;
  qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
}

void Init (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  float mIdf;
  mIdf = float (mId);
  rm.xz = 0.7 * mIdf * vec2 (1., -1.) + 35. * mod (todCur, 10.);
  rm.y = surfHt + 1.5;
  vm = 0.5 * (2. * Hashv3f (mIdf + todCur) - 1.);
  qm = vec4 (0., 0., 0., 1.);
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qm;
  vec3 rm, vm, wm, bMid;
  vec2 iFrag, canvas, b;
  float asp, bMid2;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 3) discard;
  canvas = iResolution.xy;
  todCur = iDate.w;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  surfHt = 0.8;
  surfScl = 0.25;
  mId = (pxId < 4 * nBall) ? pxId / 4 : -1;
  fBall = -1;
  if (iFrame <= 5) {
    doInit = true;
  } else {
    doInit = false;
    stDat = Loadv4 (4 * nBall);
    nStep = stDat.z;
    ++ nStep;
    stDat = Loadv4 (4 * nBall + 1);
    mPtrP.z = stDat.z;
    fBall = int (stDat.w);
    if (mPtr.z > 0. && mPtrP.z < 0. && mPtr.x > 0.5 - 0.05 / asp && abs (mPtr.y) > 0.5 - 0.05) {
      if (mPtr.y > 0.) {
        if (nStep > 100.) doInit = true;
      } else {
        if (fBall >= 0) fBall = -1;
        else fBall = int (float (nBall) * Hashff (mod (todCur, 1000.)));
      }
    }
  }
  if (doInit) {
    nStep = 0.;
    fBall = -1;
  }
  if (mId >= 0) {
    if (doInit) Init (mId, rm, vm, qm, wm);
    else Step (mId, rm, vm, qm, wm);
  }
  if (pxId == 4 * nBall + 2) {
    bMid = vec3 (0.);
    bMid2 = 0.;
    if (nStep > 0.) {
      for (int n = VAR_ZERO; n < nBall; n ++) {
        b = Loadv4 (4 * n).xz;
        bMid.xz += b;
        bMid2 += dot (b, b);
      }
      bMid.xz /= float (nBall);
      bMid.y = 0.1;
      bMid2 = sqrt (bMid2 / float (nBall) - dot (bMid.xz, bMid.xz));
    }
  }
  if (pxId < 4 * nBall) {
    kp = 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
  } else {
    kp = 4 * nBall;
    if (pxId == kp) stDat = vec4 (surfHt, surfScl, nStep, 0.);
    else if (pxId == kp + 1) stDat = vec4 (mPtr.xyz, float (fBall));
    else if (pxId == kp + 2) stDat = vec4 (bMid, bMid2);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
