  vec3 rm, vm, wm, bMid;
  vec2 iFrag, canvas, b;
  float asp, bMid2;
  int mId, pxId, kp;
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (iFrag.x >= txRow || pxId >= 4 * nBall + 3) discard;
  canvas = iResolution.xy;
  todCur = iDate.w;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  surfHt = 0.8;
  surfScl = 0.25;
  mId = (pxId < 4 * nBall) ? pxId / 4 : -1;
  fBall = -1;
  if (iFrame <= 5) {
    doInit = true;
  } else {
    doInit = false;
