    if (nStep > 0.) {
      for (int n = VAR_ZERO; n < nBall; n ++) {
        b = Loadv4 (4 * n).xz;
        bMid.xz += b;
        bMid2 += dot (b, b);
      }
      bMid.xz /= float (nBall);
