#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));}
float ln (vec2 p, vec2 a, vec2 b) {return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));}
void mainImage( out vec4 Q, in vec2 U )
{
    vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec2 h = vec2(e.z-w.z,n.z-s.z);
    vec2 g = vec2(e.y-w.y,n.y-s.y);
    vec2 f = vec2(e.x-w.x,n.x-s.x);
    vec4 b = A(U);
   	vec4 dx = 0.25*(n+e+s+w) - b;
    vec3 no = normalize(vec3(5.*h+g,-1));
    vec3 r = reflect(vec3(0,0,1),no);
    vec3 l = vec3(2.*R,3.*R.y);
    vec3 u = vec3(U,0);
    vec3 lu = l-u;
    float o = length(r-lu*dot(r,lu)/dot(lu,lu));
    Q = sin(-.1+b.z*vec4(10,1,-1,1))+ sin(.2*b.x+b.y*vec4(-1,5,4,4));
    Q *= (2.*exp(-2.*o)+60.*exp(-10.*o));
    Q = mix(Q,vec4(0),smoothstep (0.007*R.x,0.004*R.x, ln(U,vec2(0.975,0.05)*R,vec2(0.975,0.95)*R)));
    Q = mix(Q,vec4(.5,0.3,1,1),smoothstep (0.004*R.x,0.003*R.x, ln(U,vec2(0.975,0.05)*R,vec2(0.975,0.95)*R)));
	Q = mix(Q,vec4(0),smoothstep (0.019*R.x,0.015*R.x, length(U-vec2(0.975,b.w)*R)));
	Q = mix(Q,vec4(1),smoothstep (0.016*R.x,0.01*R.x, length(U-vec2(0.975,b.w)*R)));
}

#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    float a = sin(iTime+3.*length(U)), c = cos(a), s = sin(a);
    mat2 m = mat2(c,-s,s,c);
    #define a 1.
    #define o a*.7071
    vec4 n = A(U+vec2(0,a)*m)+A(U+vec2(a,0)*m)+A(U+vec2(0,-a)*m)+A(U+vec2(-a,0)*m)+A(U+vec2(-o,o)*m)+A(U+vec2(o,-o)*m)+A(U+vec2(-o,-o)*m)+A(U+vec2(o,o)*m);
    n *= .125;
    vec4 dx = n-Q;
    Q += dx*vec4(1.,.3,1.,1);

    float x = (Q.w+.11)*Q.x*Q.y*(1.-Q.y);

    Q.y = Q.y+x-0.01-.3*Q.z;
    Q.x = Q.x+0.1*Q.x*(1.-Q.x)-x;
    Q.z = Q.z*0.9 + .5*dx.y+.01*Q.y;

    Q = max(Q,0.);

    if (iMouse.x < R.x*0.95 && iMouse.z> 0. && length(U-iMouse.xy) < .15*R.y) Q.x=0.;

    if (iFrame < 1) {

        Q = vec4(1,0,0,0);
        if (length(U-0.5*R)<10.) Q.y += 1.;
    }
}

