// Shows how register allocation can kill GPU parallelism, even if the amount of computations
// are the same and the same number of registers are touched. This is the performance I get in
// my laptop as a function of the buffer size NUM: 
//
// NUM     fps
//
//  128    120
//  192    108
//  224     98
//  256     85
//  384     60
//  448     52
//  512     45
//  768     31
// 1024     24
// 2048     12

#define NUM 256   // change this

vec4 data[NUM];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int id = (int(fragCoord.x) + iFrame) % NUM;

    data[0    ] = vec4(sin(iTime+0.0), 0.0, 0.0, 1.0 );
	data[id   ] = vec4(0.0, sin(iTime+2.0), 0.0, 1.0 );
	data[NUM-1] = vec4(0.0, 0.0, sin(iTime+4.0), 1.0 );

    fragColor = data[0] + data[id] + data[NUM-1];
}
