float sstep(float g, float a, float b){
    float t = min(max((g-a)/(b-a),0.),1.);
    return t*t*(3.-2.*t);
}

vec3 rwb(float g){
    float R = max(min(min(1.+g,1.),2.-g),0.);
    float G = max(1.-abs(g),0.);
    float B = max(min(min(1.-g,1.),2.+g),0.);
    return vec3(R,G,B);
}

float f(float x, float y){
    return (y-sin(4.*x))/length(vec2(4.*cos(4.*x),1.));
}
float f2(float x, float y){
	vec2 df = vec2( f(x+0.01,y)-f(x-0.01,y), f(x,y+0.01)-f(x,y-0.01))/0.02;

    return f(x,y)/length(df);  
}

float f3(float x, float y){
	vec2 df2 = vec2( f2(x+0.01,y)-f2(x-0.01,y), f2(x,y+0.01)-f2(x,y-0.01))/0.02;
    return f2(x,y)/length(df2);
}

float f4(float x, float y){
	vec2 df3 = vec2( f3(x+0.01,y)-f3(x-0.01,y), f3(x,y+0.01)-f3(x,y-0.01))/0.02;
    return f3(x,y)/length(df3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 X = 3.*(fragCoord/(iResolution.y)- vec2(0.5*(iResolution.x)/(iResolution.y),0.5));
	float x = X.x;
    float y = X.y;

    vec2 df4 = vec2( f4(x+0.01,y)-f4(x-0.01,y), f4(x,y+0.01)-f4(x,y-0.01))/0.02;

    float g = f4(x,y)/length(df4);
    //g = f2(x,y);

    vec3 col = rwb(10.*g);

    // Axes
    if(x<0.01 && x>-0.01 || y<0.01 && y>-0.01) col *= 0.;
    if((x-1.)<0.01 && (x-1.)>-0.01 && y<0.1 && y>-0.1) col *= 0.5;
    if((x+1.)<0.01 && (x+1.)>-0.01 && y<0.1 && y>-0.1) col *= 0.5;
    if((x)<0.1 && (x)>-0.1 && (y-1.)<0.01 && (y-1.)>-0.01) col *= 0.5;
    if((x)<0.1 && (x)>-0.1 && (y+1.)<0.01 && (y+1.)>-0.01) col *= 0.5;

    // Output to screen
    fragColor = vec4(col,1.0);
}
