#define BLUR
#define BRIGHT_SPOTS
//#define STARS

float gamma=1.8;

float BRIGHT_SPOT_TRESHOLD=0.5;
int CONV_SIDE=10; //20 (bigger blur and performance comparation with separable one)
float BLUR_RADIUS=10.; //20 (bigger blur and performance comparation with separable one)

vec3 BriSp(vec3 p){
    if(p.x+p.y+p.z>BRIGHT_SPOT_TRESHOLD*3.)p=(1./(1.-p)-1.)*(1.-BRIGHT_SPOT_TRESHOLD);
    p=clamp(p,0.,500.);
    return p;
}


vec3 color(vec2 n){
    #ifdef STARS
	vec3 p=vec3(smoothstep(0.7,1.,texture(iChannel1,n).r));
    #else
    vec3 p=texture(iChannel0,n).rgb;
    #endif
    #ifdef BRIGHT_SPOTS

    p=BriSp(p);
    #endif
    return p;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 un=fragCoord;

    un.x-=iResolution.y/2.;

    #ifdef BLUR
    vec2 u_=vec2(0);
    float n=0.;
    vec3 res=vec3(0);
    for(int x=-CONV_SIDE;x<=CONV_SIDE;x++){
        for(int y=-CONV_SIDE;y<=CONV_SIDE;y++){
        	u_=un+vec2(x,y);
            if(distance(u_,un)<=BLUR_RADIUS){
            	u_/=iResolution.y;
                res+=pow(color(u_),vec3(gamma));
                n++;
            }
        }
    }
    res/=n;
    fragColor = vec4(pow(res,vec3(1./gamma)),1);
 	#else
    fragColor = vec4(color(un/iResolution.y),1.);
    #endif
}
