#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
float ln (vec2 p, vec2 a, vec2 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
float ln (vec3 p, vec3 a, vec3 b) { // returns distance to line segment for mouse input
    return length(p-a-(b-a)*dot(p-a,b-a)/dot(b-a,b-a));
}

// apply forces

vec4 X (vec4 Q, vec2 u) {
	vec4 a = A(gl_FragCoord.xy + u);
    float f = abs(Q.w-a.w)*max(0.,1.-a.w)*max(0.,1.-Q.w);
    return mix(Q,a,clamp(2.*f,0.,1.));
}
Main {
	Q = A(U);
    if (iFrame%2==0)  {
        for (int x = -1; x<=1;x++)
        for (int y = -1; y<=1;y++)
        if (abs(x)!=abs(y))
        {
            vec2 u = vec2(x,y);
            vec4 a = A(gl_FragCoord.xy + u);
            vec4 b = B(gl_FragCoord.xy + u);
            u = u/dot(u,u);
            float f = min(Q.w,1.)*.1*a.w*(a.z*(a.w-1.)+1.);
            Q.xy -= f*u/max(1.,Q.w);
            Q.z  -= Q.w*f*dot(u,a.xy);
        }
        Q.y -= .2/R.y;
        if (length(Q.xy) >1.) Q.xy = normalize(Q.xy);
        if (iFrame < 1) {
            float s = step(U.y,sin(U.x/R.x*6.2*4.)*R.y/25.+0.5*R.y);
            Q = vec4(0,0,1.+s,1.);
        }
    } else {
    	vec4 q = vec4(0);
        for (int x = -1; x <= 1; x++)
        for (int y = -1; y <= 1; y++)
        if (x!=0||y!=0)
           q += X(Q,vec2(x,y));
        Q = 0.125*q;
    }
    if (iMouse.z>0.&&length(U-iMouse.xy)<5.)
        Q = vec4(-.9,0,1,1);
    if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.)Q.xy*=0.;
}

// Conservative advect alternating x and y-direction
Main {
    Q = vec4(0); float m = 0.;
    vec2 r = iFrame%2==1?vec2(1,0):vec2(0,1);

	for (float i = -1.; i <= 1.; i++)
    {
    	vec2 u = i*r;
        vec4 a = .5*A(U+u-.5*r),
             b = A(U+u),
             c = .5*A(U+u+.5*r);
        float w1 = 1.+dot(c.xy,r)-dot(a.xy,r);
        if (w1>0.) {
            float w = clamp(dot(u,r)+.5+dot(c.xy,r),-.5,.5)-
                      clamp(dot(u,r)-.5+dot(a.xy,r),-.5,.5);
            Q += b.w*w/w1*b;
            m += b.w*w/w1;
        }
    }
    if (m > 0.) Q /= m;
    Q.w = m;
}

