float maxDist = 500.;
vec3 camRot = vec3(0, 0, 0);
vec3 lightDir = vec3(1.2,.7,.8);
vec3 lightColor = vec3(.8,.4,.3)*3.5;
vec3 ambientLight = vec3(0);

float noise(vec2 p)
{
    return fract(sin(p.x*16.+p.y*6463.)*3732.);
}

float smoothNoise(vec2 p)
{
    vec2 id = floor(p*10.);
    vec2 ld = fract(p*10.);

    ld = ld*ld*(3.-2.*ld);

    float bl = noise(id);
    float br = noise(id+vec2(1., 0.));
    float b = mix(bl, br, ld.x);

    float tl = noise(id+vec2(0., 1.));
    float tr = noise(id+vec2(1., 1.));
    float t = mix(tl, tr, ld.x);

    float bt = mix(b, t, ld.y);

    return abs(bt);
}

float ridged(float x)
{
    return abs(1.-abs(x));
}

float vulcano(float x)
{
    return x*pow((abs(sin(x*2.1))+1.)/2., 3.);
}

vec2 map(vec3 p)
{
    float a = p.y/15.;

    float mat = 0.;
    float terrainHeight=60.;

    if(p.y < 1.5)
    	return vec2(1.5-p.y, 1.);

    p+=vec3(25.5*cos(a), -1.5*sin(a)*p.y/20., 0);
    float base = vulcano(pow(ridged(smoothNoise(p.xz/1000.)), 5.))*terrainHeight;
    base += .4*vulcano(pow(smoothNoise(p.xz/520.), 5.))*terrainHeight;
        p+=vec3(25.5*cos(a), -1.5*sin(a)*p.y/20., 0)*.5;
    base += .1*pow(smoothNoise(p.xz/104.), 2.)*terrainHeight;
    //p+=vec3(25.5*cos(a), 0, 0)*2.5;
    base += .02*smoothNoise(p.xz/32.)*terrainHeight;
    base += .01*pow(smoothNoise(p.xz/16.), 2.)*terrainHeight;
    base += .002*pow(smoothNoise(p.xz/4.), 2.)*terrainHeight;

    return vec2(base-p.y, mat);
}


vec2 ray(vec3 ro, vec3 rd, float minD, float maxD, float stepSize) // x channel - distance to hit, y channel - material
{
    float t = minD;
    float lastVal = 0.; 

    while(t < maxD)
    {
        vec2 mapSample = map(ro+rd*t);
        if(mapSample.x > 0.)
            return vec2(t-(abs(mapSample.x)/(abs(mapSample.x)+abs(lastVal)))*stepSize, mapSample.y);
        t+=stepSize;
        stepSize*=1.01;
        lastVal = mapSample.x;
    }

    return vec2(maxD, 0);
}

vec3 normal(vec3 p)
{
	float delta = 0.01;
    vec3 x = vec3 (delta, 0.00, 0.00);
	vec3 y = vec3 (0.00, delta, 0.00);
	vec3 z = vec3 (0.00, 0.00, delta);

    float val = map(p).x;
    vec3 grad = vec3(val - map(p - x).x, val - map(p - y).x, val - map(p - z).x);
    return -normalize(grad);
}


vec3 lighting(vec3 n)
{
    return  max(pow(dot(normalize(lightDir), n), 3.), 0.0) * lightColor +ambientLight;
}

vec3 doFog(vec3 rgb, float dist, float maxD, vec3 rd, vec3 p)
{
    lightDir = normalize(lightDir);
    vec3 fogColor = vec3(0.12, 0.11, 0.13)*.5;
    fogColor += 2.*vec3(.7, .1, 0)*pow(1.-abs(rd.y), 6.);
    fogColor += .3*lightColor*pow(clamp(dot(rd, lightDir), 0., 1.), 3.);
    fogColor += 4.*vec3(.7, .1, 0)*clamp(2.-p.y, 0., 1.);
    if(dist == maxD)
    {
    	fogColor += vec3(.5, .4, .5)*smoothNoise(vec2(p.x, p.z)/rd.y/3000.)*smoothNoise(vec2(p.x, p.z)/5./rd.y/3000.)*pow(abs(rd.y), .5);
        fogColor += .4*lightColor*pow(clamp(dot(rd, lightDir), 0., 1.), 50.); 
    }
    return mix(rgb, fogColor, pow(dist/maxD, 1.));
}

vec3 fcknLAVA(vec3 p)
{
    float morph = sin(iTime+p.x+p.z)*smoothNoise(p.xz/10.); 

    vec3 n = normal(p);
    vec3 base = vec3(0);//lighting(n)*.5;
    p+=vec3(cos(morph), 0, sin(morph));
    base += vec3(.7, .1, 0)*smoothNoise(p.xz/10.+iTime/55.);
    base += 2.*vec3(.7, .4, 0)*pow(smoothNoise(p.xz/10.+iTime/55.), 10.);
    base += 2.*vec3(.7, .7, .7)*pow(smoothNoise(p.xz/10.+iTime/55.), 30.);

    return base;
}

vec3 terrainColor(vec3 p)
{
    vec3 n = normal(p);
    float xAlpha = pow(abs(n.x), 2.);
    float yAlpha = pow(abs(n.y), 7.);
    float zAlpha = pow(abs(n.z), 2.);
    vec3 terrainColor = vec3(0);
    terrainColor += texture(iChannel0, vec2(p.z, p.y)/7.).xyz*xAlpha*.5;
    terrainColor += .3*texture(iChannel1, vec2(p.x, p.z)/10.).xyz*yAlpha;
    terrainColor += .2*texture(iChannel1, vec2(p.x, p.z)/3.).xyz*yAlpha;
    terrainColor += texture(iChannel0, vec2(p.x, p.y)/7.).xyz*zAlpha*.5;
    terrainColor+=texture(iChannel0, vec2(0, p.y)/3.).xyz*.2;
    return terrainColor*lighting(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -  iResolution.xy*.5)/iResolution.y;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float lastXInput = 0.;
    float lastYInput = 0.;
    float deltaXInput = iMouse.x-lastXInput;
    float deltaYInput = iMouse.y-lastYInput;
    camRot+=vec3(deltaYInput/100., deltaXInput/100., 0);
    vec3 camP = vec3(50, 5., 170.);
    vec3 camDir = normalize(vec3(sin(camRot.y), sin(camRot.x), cos(camRot.y)));
    vec3 camRight = cross(camDir, vec3(0, 1., 0));
    vec3 rd = normalize(camDir + camRight*1.*uv.x + vec3(0,1.,0)*1.*uv.y);
    vec2 rayResult = ray(camP, rd, .3, maxDist, 0.1);
    vec3 p = camP+rd*rayResult.x;
	vec3 col = vec3(0);

    col = doFog(mix(fcknLAVA(p), terrainColor(p), clamp(p.y-1.1, 0., 1.)), rayResult.x, maxDist, rd, p);

    // Output to screen
    fragColor = vec4(col,1.0);
    lastXInput = iMouse.x;
    lastYInput = iMouse.y;
}
