void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    col = texture(iChannel0,fragCoord/iResolution.xy).xyz;

    col *= vec3(0.95,0.98,1.04);
    col *= exposure;

    col = mix(col,smoothstep(0.,1.,col*vec3(1.1,1.,0.9)),0.4);
    col = mix(acesFilm(col), col, 1.);
    col *= 1. - dot(uv,uv*0.4)*2.1;

    col = pow(col,vec3(0.454545));

    fragColor = vec4(col,1.0);
}

bool hit = false;

// Terrain
#define marchSteps 240
//#define marchEps  (mix(0.001,0.06,smoothstep(1.,0.,exp(-t*0.3) - 2.)))
#define marchEps  (mix(0.001,0.26,smoothstep(0.,1.,t*0.1 - 2.)))


#define planetSz 2984.
#define atmoSz (planetSz/63.)

#define groundOffs vec3(0,1.3,0)

#define exposure 1.

// Clouds

#define cloudsLowerLimit 7.
#define cloudsHigherLimit 17.

#define cloudSteps 20.
#define volumetricDithAmt 5.6

// Wind

#define windSteps 5.
// Atmosphere
#define sunCol vec3(1,0.9,0.6)*1.

#define itersAtmo 14.
#define itersOptic 14.
const float redLightLen = 740.;
const float greenLightLen = 550.;
const float blueLightLen = 450.;

const float transStrength = 0.02;

float densFalloff = 1.5;


#define SSS(a) clamp(map(p + sunDir*a).x/a,0., 1.)
#define ao(a) clamp(map(p + (n*0.8 + sunDir)*a).x/a,0., 1.)

#define pi acos(-1.)

#define tau (2.*pi)
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define pmod(p,a) mod(p - 0.*a,a) - 0.5*a
float valueNoise(vec3 p, float pw);
