float valueNoise(vec3 p, float pw){

	vec3 s = vec3(1., 25, 75);

	vec3 ip = floor(p); // Unique unit cell ID.
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);

	p -= ip; // Cell's fractional component.

    // A bit of cubic smoothing, to give the noise that rounded look.
    if(pw == 1.){
        p = p*p*(3. - 2.*p); 
    } else {

    	p = p*p*(p*(p * 6. - 15.) + 10.);
    }

    //p = smoothstep(0.,1.,p);
    // Smoother version of the above. Weirdly, the extra calculations can sometimes
    // create a surface that's easier to hone in on, and can actually speed things up.
    // Having said that, I'm sticking with the simpler version above.
	//p = p*p*(p*(p * 6. - 15.) + 10.);
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);

    // Interpolating along Y.
    h.xy = mix(h.xz, h.yw, p.y);

    // Interpolating along Z, and returning the 3D noise value.
    return mix(h.x, h.y, p.z); // Range: [0, 1].

}

vec3 hash3(vec3 p) {
	p = vec3(dot(p, vec3(127.1, 311.7, 74.7)),
			dot(p, vec3(269.5, 183.3, 246.1)),
			dot(p, vec3(113.5, 271.9, 124.6)));

	return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

float r21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 r23(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}
float turbulentNoise(vec3 p, bool md) {
	p += 8.;
    vec3 i = floor(p);
	vec3 f = fract(p);

	vec3 u = f * f * (3.0 - 2.0 * f);

    //f = u;
	float n0 = dot(hash3(i + vec3(0.0, 0.0, 0.0)), f - vec3(0.0, 0.0, 0.0));
	float n1 = dot(hash3(i + vec3(1.0, 0.0, 0.0)), f - vec3(1.0, 0.0, 0.0));
	float n2 = dot(hash3(i + vec3(0.0, 1.0, 0.0)), f - vec3(0.0, 1.0, 0.0));
	float n3 = dot(hash3(i + vec3(1.0, 1.0, 0.0)), f - vec3(1.0, 1.0, 0.0));
	float n4 = dot(hash3(i + vec3(0.0, 0.0, 1.0)), f - vec3(0.0, 0.0, 1.0));
	float n5 = dot(hash3(i + vec3(1.0, 0.0, 1.0)), f - vec3(1.0, 0.0, 1.0));
	float n6 = dot(hash3(i + vec3(0.0, 1.0, 1.0)), f - vec3(0.0, 1.0, 1.0));
	float n7 = dot(hash3(i + vec3(1.0, 1.0, 1.0)), f - vec3(1.0, 1.0, 1.0));

	float ix0 = mix(n0, n1, u.x);
	float ix1 = mix(n2, n3, u.x);
	float ix2 = mix(n4, n5, u.x);
	float ix3 = mix(n6, n7, u.x);

	float ret = mix(mix(ix0, ix1, u.y), mix(ix2, ix3, u.y), u.z) * 0.5 + 0.5;
	ret = ret * 1.;

    //ret = 1.- ret;
    //ret = abs(ret);
    if (md)
        ret = mix(ret,smoothstep(0.3,1.,ret*0.8),0.6);
    return ret;
}



vec2 sphIntersect( in vec3 ro, in vec3 rd, in vec3 ce, float ra )
{
    vec3 oc = ro - ce;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - ra*ra;
    float h = b*b - c;
    if( h<0.0 ) return vec2(-1.0); // no intersection
    h = sqrt( h );
    //return -b+h;
    //return max(max(-b-h,0.),max(-b+h,0.));
    return vec2( -b-h, -b+h );
}
float plaIntersect( in vec3 ro, in vec3 rd, in vec4 p )
{
    return -(dot(ro,p.xyz)+p.w)/dot(rd,p.xyz);
}


float atmosphericDensity( vec3 p){
    float fact = (p.y - planetSz )/atmoSz;
    fact = max(fact, 0.0001);
    return exp(-fact*densFalloff)*pow(max(1. - fact,0.),0.04);//*pow(smoothstep(0.95,0.84,fact),1.);
}

float opticalDepth(vec3 p, vec3 rd, float len){
    float stSz = len / (itersOptic-1.);
    float depth = 0.;
    for(float opticIdx = 0.; opticIdx < itersOptic; opticIdx++ ){
        depth += atmosphericDensity(p) * stSz;
        p += rd*stSz;
     }
    return depth;
}


vec3 scatteringCoefficients = transStrength*vec3(
    pow(400./redLightLen,4.),
    pow(400./greenLightLen,4.),
    pow(400./blueLightLen,4.)
);
vec3 getAtmosphere(vec3 ro, vec3 rd, float t, out float opticalDepthView, vec3 sunPos){
    vec3 accumAtmo = vec3(0);
    float atmoMarchLen = 0.;

    vec3 offs = vec3(0,planetSz ,0);
    vec3 p = ro;
    sunPos += offs;

    p += offs*1.;


    float lenViewDirToEndOfAtmosphere = sphIntersect( p, rd, vec3(0), planetSz + atmoSz ).y;

    if(hit){
        atmoMarchLen = mix(t,lenViewDirToEndOfAtmosphere,smoothstep(0.,1.,t/50. - 1.));
    } else {
        atmoMarchLen = lenViewDirToEndOfAtmosphere;
    }
    float stepSz = atmoMarchLen/(itersAtmo - 1.);


    for(float atmoIdx = 0.; atmoIdx < itersAtmo ; atmoIdx++ ){
        vec3 dirToSun = normalize(sunPos - p);
        float lenSunDirToEndOfAtmosphere = sphIntersect( p, dirToSun, vec3(0), planetSz + atmoSz ).y;
        lenViewDirToEndOfAtmosphere = sphIntersect( p, -rd, vec3(0), planetSz + atmoSz ).y;

        float opticalDepthSun = opticalDepth(p, dirToSun, lenSunDirToEndOfAtmosphere);
        opticalDepthView = opticalDepth(p, -rd, stepSz*atmoIdx);

        float localDens = atmosphericDensity(p);


        vec3 transmittance = exp(-(opticalDepthSun + opticalDepthView) * scatteringCoefficients);        
        accumAtmo += transmittance * localDens * scatteringCoefficients * stepSz;

        p += rd * stepSz;
    }

    return accumAtmo;
}

mat3 getRd(vec3 ro, vec3 lookAt, vec2 uv){

    uv *= 0.7;
    vec3 dir = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0,1,0),dir));
    vec3 up = normalize(cross(dir,right));

    return mat3(right,up,dir); 
    //return normalize(dir + right*uv.x + up*uv.y);
}


// Tri-Planar blending function. Based on an old Nvidia tutorial.
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - 0.2)*7., 0.001); // max(abs(n), 0.001), etc.
    n /= (n.x + n.y + n.z );  

	return (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
}

vec3 getRdSpherical(inout vec2 uv){

    // polar coords
    uv = vec2(atan(uv.y,uv.x),length(uv));

    vec2 ouv = uv;
    uv += 0.5;
    uv.y *= pi;


    // parametrized sphere
    vec3 offs = vec3(cos(uv.y)*cos(uv.x),sin(uv.y),cos(uv.y)*sin(uv.x));

    // insert camera rotations here
    offs.yz *= rot(-(1.)*pi);

    //vec3 lookAt = ro + offs;
    //vec3 v = normalize(lookAt - ro);
    vec3 v = offs;
    //uv = ouv;
    //uv.x = v.x;
    //uv.y = v.y;

    return v;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

vec3 getNormal(vec3 p);
float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k );
float valueNoiseCheap(in vec3 p,float pw);

 float t = 0.;

float mapClouds(vec3 p){
    float currHeight = p.y;

    p *= 0.25;
    float cloudsRange = (cloudsHigherLimit - cloudsLowerLimit)*0.494;
    float cloudsMid = cloudsLowerLimit + cloudsRange;
    float f = turbulentNoise(p*0.6, true);
    float fb = turbulentNoise(p*1.4, true);
    f = (f*0.4 + turbulentNoise(p*0.5 + f*2. - fb*1.4 + iTime, true))*turbulentNoise(p*0.2 + 5.+ f*4., true);
    f *= pow( smoothstep( 1.,0., abs(currHeight - cloudsHigherLimit + cloudsRange)/cloudsRange*0.9), 2.29);

    return f * 1.;
}



float grainNoise;

vec2 getGround(vec3 p){
    float d = p.y;
    vec3 pp = vec3(p.x,4.,p.z)*0.6;
    pp.x *= 0.5;
    float n = turbulentNoise(pp, true);
    float on = n;
    n += turbulentNoise(pp*3., true)/2.*pow(n,0.4);
    n += turbulentNoise(pp*3. + 4., true)/2.*pow(n,0.2);
    n += turbulentNoise(pp*4., true)/2.*pow(n,0.4);
    n += turbulentNoise(pp*6., true)/2.*pow(on,1.4);

    //n = pow((n),1.9);

    d -= n*0.2;

    pp = vec3(p.x,1.,p.z)*0.2 + 3.;
    float h = valueNoise(pp,1.);
    d -= h*1.3;

    grainNoise = smoothstep(0.3,0.85,turbulentNoise(vec3(p.x,0.,p.z)*(130.), false));

    d -= grainNoise*smoothstep(1.,0., t*0.04 )*0.0002;



    return vec2(d,1.);
}


vec2 getRocks(vec3 p){
    float d = 10e4;

    float n = turbulentNoise(p*0.2, false);

    n = smoothstep(0.4,1.,n);
    n += n*texture(iChannel1,p.xz*0.2).x*0.24;


    d = p.y - n*1.7;
    //n -= tex3D( sampler2D tex, in vec3 p, in vec3 n );


    return vec2(d,2.);
}

vec2 map(vec3 p){
    vec2 d = vec2(10e5);
    vec2 gr = getGround(p);
    float og = gr.x;
    vec2 rocks = getRocks(p);

    rocks.x -= gr.x*0.5;
    gr.x = opSmoothUnion( gr.x, rocks.x, 0.1 );
    d = dmin(d,gr.x,gr.y);

    d = dmin(d,rocks.x,rocks.y);
    d.y = mix(1.,2.,smoothstep(0.,1.,exp(-rocks.x) - exp(-og*3.1)*1.1));

    if(d.y == 1. || d.y == 2.){
    }
    return d;
}


vec3 getSun(vec2 uv, vec2 sunPos);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 prevFrame = texture(iChannel2,fragCoord/iResolution.xy);

    // some taa
    vec2 taaidx = r23(vec3(fragCoord,float(iFrame)))*4.;
    fragCoord += float(iMouse.z>0.)*.6*vec2(sin(float(taaidx.x)*pi/4.),cos(float(taaidx.x)*pi/4.))*taaidx.y/4.;


    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;


    vec3 col = vec3(0);



    vec3 sunPos = vec3(1,.6 + sin(iTime)*.12*0.,1.)*2300.;
    vec3 sunDir = normalize(sunPos);


    vec3 ro = vec3(0);

    ro.z += iTime;




    vec3 lookAt = vec3(0,0,ro.z + 15.*float(iMouse.z < 1.));
    if (iMouse.z > 0.) {
        vec2 muv = iMouse.xy/iResolution.xy;

        ro.xz -= vec2(cos(muv.x*6.),sin(muv.x*6.))*10.;

    }    
    ro.y -= getGround(ro).x;
    ro += groundOffs;
    lookAt.y -= getGround(lookAt).x;
    lookAt += groundOffs;


    mat3 vp = getRd(ro,lookAt,uv);

    vec3 rd = vec3(uv,1.)*vp;

    //vec2 sunViewSpace = sunDir.xy;


    // Marching

    vec3 p = ro;
    vec2 d;
    for(int i = 0; i < marchSteps ; i++){
        d = map(p);


        if(d.x < marchEps){
            hit = true;
            break;
        } else if (t > 59.){
            break;
        }

        p = ro + rd*(t += d.x);
    }


    float depthView;
    vec3 atmosphere = getAtmosphere(vec3(0,ro.y - 0.,0), rd, t, depthView, sunPos);


    // Clouds

    float lowerCloudLimitDist = plaIntersect( ro - vec3(0,cloudsLowerLimit,0), rd, vec4(0,-1,0,0) );
    float higherCloudLimitDist = plaIntersect( ro - vec3(0,cloudsHigherLimit,0), rd, vec4(0,-1,0,0) );

    float volumetricDith = r21(fragCoord + sin(iTime*20.)*20.)*volumetricDithAmt;
    vec3 cloudP = ro + rd * ( lowerCloudLimitDist + volumetricDith );
    float cloudLength = higherCloudLimitDist - lowerCloudLimitDist; 
    float cloudStepSz = cloudLength/(cloudSteps);

    float cloudDensTotal = 0.;
    vec3 cloudAccum = vec3(0.);


    for(float i = 0.; i < cloudSteps ; i++){
        float d = mapClouds(cloudP);

        float difffact = clamp( d*1. - mapClouds(cloudP + sunDir*1.4)*1.1  + 0.12, 0., 1. );
        vec3 diff = mix( atmosphere*0.1 + vec3(0.04,0.11,0.2)*(0.4 - smoothstep(0.,1.,1. - atmosphere*1.8)*0.4), (vec3(1,0.9,0.9)*1. + sunCol*0.4 + atmosphere*.4)*0.5, difffact );
        vec3 absorption = mix( vec3(1,0.9,0.8), vec3(1,0.9,0.7)*0.5, clamp( cloudDensTotal*0.5, 0., 1. ) );
        vec3 fringe = vec3(0.9,0.8,0.5)*clamp( 1. - d*4., 0.,1.);

        d = d*(1.-cloudDensTotal)*cloudStepSz;

        cloudDensTotal += d;
        cloudAccum += d*(diff*3.7*absorption + fringe*(0.1 + diff*0.9)*2.64);

        // map(cloudP).x < 0.


        if( cloudDensTotal > 1.){
            break;
        }
        cloudP += rd*cloudStepSz;
    }


    // Wind
    vec3 windP = ro + rd*volumetricDith*0.00 ;
    float windStepSz = min(t,14.)/windSteps;

    float windAccum = 0.;
    //vec3 windAccum = vec3(0.);

    for(float i = 0.; i < windSteps ; i++){
        vec3 wp = windP*0.51 - vec3(-iTime*3.,smoothstep(0.,1.,windP.y*0.5 - 1.9),0.);
        float dens = valueNoise(wp,1.);
        dens = pow(dens,1.1)*0.035;
        dens *= smoothstep(1.,0.,windP.y*0.15 + 0.4);

        windAccum += dens*(1.-windAccum)*windStepSz;

        if( windAccum > 1.){
            break;
        }
        windP += rd*windStepSz;
    }


    // Coloring

    vec3 hitCol = vec3(0);
    vec3 ambientCol = atmosphere*1.;
    float ambianceScale = 0.4;
    if(hit){
        vec3 n = getNormal(p);
        vec3 hf = normalize(sunDir - rd);
        float diff = max(dot(n,sunDir),0.);
        float spec = pow(max(dot(n,hf),0.),6.);
        float fres = pow( 1. - max(dot( n, -rd),0.001),5.);
        fres = max(fres,0.);

        vec3 albedo = vec3(1.,0.5,0.2)*0.8;

        float rockiness = d.y - 1.;
        rockiness = clamp(rockiness,0.,1.);

        albedo = mix(albedo,vec3(0.1,0.1,0.1),rockiness);
        //float shad = softshadow( p - n * 0.04, sunDir, 1., 5., 2. )*0.;
        hitCol += albedo*(sunCol + atmosphere);
        float AO = ao(1.9)*ao(0.2)*ao(4.2)*ao(0.8)*2.;

        float shad = diff;
        vec3 sand = hitCol;
        vec3 grains = grainNoise*(sunCol + hitCol*0.9);
        grains = mix(grains*0.1 + 0.1, vec3(0.3),smoothstep(1.,0.,exp(-max(t,0.001)*0.05)));
        {

            sand += (spec + fres*0.4)*grains*.4;
            sand = mix( (sand*vec3(0.4,0.1,0.1) + ambientCol*0. + sunCol*(1. - diff)*0.04)*ambianceScale,sand*2.,shad);
            sand = mix( (sand*0. + ambientCol*0.04)*ambianceScale,sand,AO);
        }
        vec3 rock = hitCol;
        {
            vec3 grains = grainNoise*(sunCol + hitCol*0.9);
            grains = mix(grains*0.1 + 0.1, vec3(0.3),smoothstep(1.,0.,exp(-t*0.05)));

            rock += (spec + fres*0.4)*grains*.4;
            rock = mix( (rock*vec3(0.4,0.1,0.1) + ambientCol*0. + sunCol*(1. - diff)*0.04)*ambianceScale,rock*2.,shad);
            rock = mix( (rock*0. + ambientCol*0.04)*ambianceScale,rock,AO);
        }

        hitCol = mix(sand,sand,rockiness);
        hitCol = sand;

    }


    // Compositing

    col += hitCol;

    if(hit){
       atmosphere *= 1.-pow(exp(-(t)*.04 ),2.);
    }

    atmosphere += getSun(rd.xy, sunDir.xy);
    float depthViewFac = smoothstep(0.,1.,exp(-depthView*0.02) + exp(-t*0.4));

    col = col * depthViewFac + atmosphere; 

    if (!hit  && lowerCloudLimitDist > 0.){
        cloudAccum = mix(cloudAccum,col,clamp(1.-exp(-lowerCloudLimitDist*0.01 + 0.4),0.,1.));
        col = mix(col,cloudAccum*1. , pow(clamp(cloudDensTotal*1. - 0.,0.,1.),4.));
    }
    windAccum *= smoothstep(0.,1.,t*0.2 - 1.);
    col = (col - windAccum) + 4.*windAccum*vec3(1,0.77,0.5);

    /*

    col *= vec3(0.95,0.98,1.04);
    col *= exposure;

    col = mix(col,smoothstep(0.,1.,col*1.3),0.3);
    col = mix(acesFilm(col), col, 1.);
    col *= 1. - dot(uv,uv*0.4)*2.1;

    col = pow(col,vec3(0.454545));
    */
    if(iFrame >1 && iMouse.z < 1.){
        fragColor = mix(prevFrame, col.xyzz,0.25);
    } else {
        fragColor = col.xyzz;
    }
    //fragColor = vec4(col,1.0);
}


vec3 getNormal(vec3 p){
      vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*map(p+e*0.001).x;
    }
    return normalize(n);
}


vec3 getSun(vec2 uv, vec2 sunPos){

    vec2 sunUVOriginal = uv;

    vec2 sunUV = uv - sunPos;
    // sun
    vec3 sun = sunCol*smoothstep(0.07,0.,length(sunUV));
    sun += sunCol*vec3(1.,0.4,0.6)*smoothstep(0.1,0.,length(sunUV));
    sun += sunCol*vec3(0.7,0.4,0.6)*smoothstep(0.3,0.,length(sunUV))*0.5;
    sun += sunCol*vec3(0.3,0.4,0.6)*smoothstep(0.6,0.,length(sunUV))*0.35;


    // rays


    vec3 sunRays = 0.4*sunCol * smoothstep(0.015*(1. + smoothstep(1.,0.,abs(sunUV.x)) ) ,0.,abs(sunUV.y))*smoothstep(0.5,0.,abs(sunUV.x));

    for(float i = 0.; i < 8.; i++){
        sunUV *= rot(pi/8./1.);
        float mda = sin(i*pi/4.);
        float mdb = sin(i*pi/2.);
        float w = 0.03;
        float l = 0.1;
        sunRays += (sunCol) *
            mix(.8,.1,smoothstep(0.,0.25 +  sin(i*pi/ 4. + iTime)*0.1,length(sunUV))) *
            smoothstep(w + mda*w/4.,0.,abs(sunUV.y))*smoothstep((l + mdb*0.1)*1.5,0.,abs(sunUV.x));
    }   
    sunUV = sunUVOriginal - sunPos;
    vec3 flares = vec3(0);
    vec2 toMid = sunPos;
    vec2 dirToMid = -normalize(toMid);
    float lenToMid = length(toMid);

    // flares
    for(float i = 0.; i < 12.; i++){
          sunUV -= 2.*lenToMid*dirToMid/12.;
          float dfl = length(sunUV) - (0.1 + 0.1*sin(i*5.))*0.5;
          dfl *= 0.5;
          vec3 flare = 0.01*(sunCol)*smoothstep(0.02,0.,dfl);
          flare += 0.003*(sunCol*sunCol)*smoothstep(0.01,0.,abs(dfl - dFdx(uv.x)));
          flares += flare*abs(sin(i*10.));
    }   


    return sun + sunRays + flares*3.*sunCol;
}

float valueNoiseCheap(in vec3 p,float pw)
{
    vec3 ip = floor(p);
    vec3 fp = fract(p);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec2 rz = textureLod( iChannel0, (tap+0.5)/256.0, 0.0 ).yx;
	return mix( rz.x, rz.y, fp.z );
}
