float Circle(vec2 uv, vec2 p, float r, float blur) {
	float l = length(uv-p);
    float f = smoothstep(r,r-blur, l);
    return f;
}

vec3 Smiley(vec2 uv, vec2 p, float size, float blur) {
    uv -= p;
    uv/=size;
    float f = Circle(uv, vec2(0.,0.), 0.5, blur);
    float eye = Circle(uv, vec2(0.2,0.2), 0.1, blur);
	f -= eye;
    float eye2 = Circle(uv, vec2(-0.2,0.2), 0.1, blur);
    f -= eye2;

    float mouth = Circle(uv, vec2(0.,0.01), 0.4, blur);
    mouth -= Circle(uv, vec2(0.,0.12), 0.4, blur);
    f -= mouth;
    vec3 color = vec3(1.0, 1.0, 0.)*f;
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
	uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
   	 		log(iSampleRate);
    uv.x += sin(iTime);
    float x = uv.x;
    float m=0.;
   // m = sin(iTime+x*8.)*.1;
    float y = uv.y-m;
    float blur =0.01;
   // blur =abs(sin(uv.x+iTime))*0.4;
    // Time varying pixel color
    vec3 f = vec3(0.);
    f+= Smiley(vec2(x,y), vec2(0.), 0.8, blur); 

    fragColor = vec4(f,1.0);

}
