#define hue(h) clamp( abs( fract(h + vec4(3,2,1,0)/3.) * 6. - 3.) -1. , 0., 1.)

vec2 rand( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;        

    uv *= 7.5;

    vec2 iuv = floor(uv);
    vec2 guv = fract(uv);

    float mDist = 2.1;

    for (float y= -1.; y <= 1.; y++) {
        for (float x= -1.; x <= 1.; x++) {            
            vec2 neighbor = vec2(x, y);            
            vec2 point = rand(iuv + neighbor);			
            point = 0.5 + 0.5*sin(iTime*.5 + 6.2831*point);		
            vec2 diff = neighbor + point - guv;            
            float dist = length(diff) * (1.0 + rand(iuv + neighbor + 100.).x*.25);

            mDist = min(mDist, dist)*.85;
        }
    }        

    mDist *= 7.5;

    float v = fract(floor(mDist*10.)/10.);

    vec2 vr = rand(iuv);
    vec3 col = vec3(hue(vr.x + vr.y*20.).rgb) * (v > .5 ? .9 : .25);    

    fragColor = vec4(col,1.0);    
}
