float N21(vec2 p)
{
    return fract(sin(p.x * 102.34 + p.y * 943.23 ) * 1934.22 + iTime * .1);
}


float pnoise(vec2 p)
{
    vec2 gv = fract(p);
    vec2 id = floor(p);

    float bl = N21(id);
    float br = N21(id + vec2(1., 0.));
    float b = mix(bl, br, gv.x);

    float tl = N21(id + vec2(0. ,1.));
    float tr = N21(id + vec2(1., 1.));
    float t = mix(tl, tr, gv.x);

    return length(mix(b, t, gv.y));
}

float fbm(vec2 p)
{
    float c = pnoise(p);
    c += pnoise(p * 2.) * .5;
    c += pnoise(p * 4.) * .25;
    c += pnoise(p * 8.) * .125;
    //c += pnoise(p * 16.) * .0725;
    return c/1.5;
}


float box(vec2 p, vec2 size, float s)
{
    size.x+=fbm(p * 80.) * .02;
    size.y+=fbm(p * 80.) * .02;
    p = abs(p);

    float x = smoothstep(size.x + s, size.x, p.x);
    float y = smoothstep(size.y+ s, size.y , p.y);
    return x * y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    vec3 box1 = box(uv + vec2(-.03,0.), vec2(.32,.45), .01) * vec3(.4, .1,.0);
    vec3 box2 = box(uv + vec2(.6,0.), vec2(.25,.43), .01) * vec3(.5, .0,.1);
    vec3 box3 = box(uv + vec2(-.6,0.), vec2(.2,.42), .01) * vec3(.9, .6,.35);
    vec3 col = box1 + box2 + box3;
    col *= texture(iChannel0, vec2(pnoise(uv))).rgb;

    // Output to screen
    fragColor = vec4(col,1.0) * 1.5;
}
