// The shader will show solid colors while the texture is loading and
// will switch to show the actual texture content when it is ready.

#define textureLoaded(s) (textureSize(s,0).x>1)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // pixel coords
    ivec2 p = ivec2(fragCoord-0.5);

    // sample all textures
    vec3 colA = texelFetch( iChannel0, p%ivec2(iChannelResolution[0].xy), 0 ).xyz;
    vec3 colB = texelFetch( iChannel1, p%ivec2(iChannelResolution[1].xy), 0 ).xyz;
    vec3 colC = texelFetch( iChannel2, ivec3(p,0)%ivec3(iChannelResolution[2].xyz), 0 ).xyz;
    vec3 colD = texture(    iChannel3, vec3(p,400.0) ).xyz;

    // mark then as "loading" if they are still downloading
    if( !textureLoaded(iChannel0) ) colA = vec3(1.0,0.0,0.0);
    if( !textureLoaded(iChannel1) ) colB = vec3(0.0,1.0,0.0);
    if( !textureLoaded(iChannel2) ) colC = vec3(0.0,0.0,1.0);
    if( !textureLoaded(iChannel3) ) colD = vec3(1.0,1.0,0.0);

    // tile all textures in screen
    int xres = int(iResolution.x);
    vec3 col = (4*p.x<1*xres) ? colA :
               (4*p.x<2*xres) ? colB :
               (4*p.x<3*xres) ? colC :
                                colD;
    // output color
    fragColor = vec4(col,1.0);
}
