//in the long run [compatibility] is likely becoming a subset of [cull] (but not a subset of [CSG])
//for large scale optimization/compatibility it all comes down to (frustrum) culling.
//
//csg (fuzzy logic) boolean is DIFFERENT; is best expressed with max(a,b)==-min(-a,-b)
//eucliteanDistanctZOPoint here [adds +1 negation] to all logic, so [CSG] is the negative/complement of [cull]
//quite common namespace of:  https://www.shadertoy.com/view/Xsc3WX
float csgNEG(float x){return -x;}//{return csgN(x,x);}
float csgAND(float a,float b){return max(a,b);}//AND=NOT(NAND(A,B))
float csgN(float a,float b){return -max(a,b);}//NAND
float csgOR (float a,float b){return -max(-a,-b);}//OR=NAND(NOT(A),NOT(B))
float csgXOR(float a,float b){float o=-max(a,b);return -max(-max(a,o),-max(b,o));}
float csgSUB(float a,float b){return -max(a,-b);}//SUB=AND(A,NOT(B))=NOT(NAND(A,NOT(B)))
//XOR=NAND(NAND(A,NAND(A,B)),NAND(B,NAND(A,B)))

//intBitsToFloat() uintBitsToFloat() (and their pseudo-inverses) are glES300 exclusive.
//due to NaN ambiguity, they can only be pÃ¼seudo-inverse, and only bijectively address 30 of 32 bits.
//getting half-decently substituting bitwise functions for type float is a tricky mess:
//exp2(b) == pow(2.,b) is asserted to be true, where exp2() is likely faster AND more precise.

//https://www.shadertoy.com/view/Mt2GWD | https://www.shadertoy.com/view/lsK3D1
//return [0{{1] of bit b[0..23] from (whole number) float [a]=floor(A); //assert 23 bit base of ieee32bit float
float getBit(float a,float b){return floor(mod(floor(a/exp2(floor(b))),2.));}
//https://www.shadertoy.com/view/Xsc3WX
//assert a=floor(A) b=floor(B), return [b]-th bit of [a], range [0||1]  domain of b is shifted to [-1..22], it includes a signBit as 24th bit!
float getBit24(float a,float b){return getBit(a,clamp(b,-1.,22.));} //worksafe clamping
//we can only get 24 base-bits (+1 sign bit) of a 32 bit float, getting 30 bit of it requires other (less compatible) command; intBitsToFloat()

//mod() is not to be trusted:
//float getBit0(float a){return floor(a/1.-floor(a*.5/1.)*2.);}
//float getBit1(float a){return floor(a/2.-floor(a*.5/2.)*2.);}
//float getBit2(float a){return floor(a/4.-floor(a*.5/4.)*2.);}
//float getBit3(float a){return floor(a/8.-floor(a*.5/8.)*2.);}
//above is 4 seperate bits explicitly, below is (up to) any 4 bits at once. 
//below getBits4() generalizes all the above 
//
//a=floor(A); b=pow(2.,floor(vec4(B,C,D,E)))  //or less domains
//return  range[0..1] of [A] after masking and shifting by single bit masks (B,C,D,E)
#define getBits4(a,b) floor(((a)/(b))-(floor((a)*.5/(b))*2.))
//works, because getBits4() returns a vector, that we can min() max() foreach domain!
//bitAND(a,b,c) return a&b&(c.x+c.y+c.z+c.w)
//return bitwise AND of 4bits of [a] and [b], masked by [c]
//bitANDm4(float a,float b,vec4 c){c*=vec4(equal(getBits4(a,c),getBits4(b,c)));return c.x+c.y+c.z+c.w;}
float bitAND(float a,float b,vec4 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=min(getBits4(a,c),getBits4(b,c));return c.x+c.y+c.z+c.w;}//AND is min()
float bitAND(float a,float b,vec3 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=min(getBits4(a,c),getBits4(b,c));return c.x+c.y+c.z;}//AND is min()
float bitAND(float a,float b,vec2 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=min(getBits4(a,c),getBits4(b,c));return c.x+c.y;}//AND is min()
float bitAND(float a,float b,float c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){return c*min(getBits4(a,c),getBits4(b,c));}//AND is min()
//bitOR(a,b,c) return (a|b)&(c.x+c.y+c.z+c.w)
//return bitwise OR of 4bits of [a] and [b], masked by [c]
float bitOR(float a,float b,vec4 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=max(getBits4(a,c),getBits4(b,c));return c.x+c.y+c.z+c.w;}//OR is max()
float bitOR(float a,float b,vec3 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=max(getBits4(a,c),getBits4(b,c));return c.x+c.y+c.z;}//OR is max()
float bitOR(float a,float b,vec2 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=max(getBits4(a,c),getBits4(b,c));return c.x+c.y;}//OR is max()
float bitOR(float a,float b,float c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){return c*max(getBits4(a,c),getBits4(b,c));}//OR is max()
//instead of notEqual, can use something like csgXOR(), except its complement!
//bitXOR(a,b,c) return (a^b)&(c.x+c.y+c.z+c.w)
//return bitwise XOR of 4bits of [a] and [b], masked by [c]
float bitXOR(float a,float b,vec4 c//assert a=floor(A) b=floor(B) c=exp2(floor(C))
){c*=vec4(notEqual(getBits4(a,c),getBits4(b,c)));return c.x+c.y+c.z+c.w;}
float bitXOR(float a,float b,vec3 c
){c*=vec3(notEqual(getBits4(a,c),getBits4(b,c)));return c.x+c.y+c.z;}
float bitXOR(float a,float b,vec2 c
){c*=vec2(notEqual(getBits4(a,c),getBits4(b,c)));return c.x+c.y;}
float bitXOR(float a,float b,float c
){return c*float(getBits4(a,c)!=getBits4(b,c));}
//return bitwiseAND of [a] and [b], masked by 4 smallest bits==[15]==pow(2,4)-1
float bitANDmask15(float a,float b//assert a=floor(A) b=floor(B)
){return bitAND(a,b,vec4(1,2,4,8));}
//return bitwiseAND of [a] and [b], masked by 8 smallest bits==[255]==pow(2,8)-1
float bitANDmask255(float a,float b
){return bitAND(a,b,vec4(1,2,4,8))+bitAND(a,b,vec4(1,2,4,8)*16.);}
//return bitwiseAND of [a] and [b], masked by 16 smallest bits==[65535]==pow(2,16)-1
float bitANDmask65535(float a,float b
){return bitAND(a,b,vec4(1,2,4,8)     )+bitAND(a,b,vec4(1,2,4,8)*16.)
         +bitAND(a,b,vec4(1,2,4,8)*256.)+bitAND(a,b,vec4(1,2,4,8)*4096.);}
//return bitwiseAND of [a] and [b], masked by 24 smallest bits==[16777215]==pow(2,24)-1
float bitANDmask16777215(float a,float b
){return bitAND(a,b,vec4(1,2,4,8)        )+bitAND(a,b,vec4(1,2,4,8)*16.)
         +bitAND(a,b,vec4(1,2,4,8)*256.  )+bitAND(a,b,vec4(1,2,4,8)*4096.)
         +bitAND(a,b,vec4(1,2,4,8)*65536.)+bitAND(a,b,vec4(1,2,4,8)*1048576.);}
//return bitwiseOR of [a] and [b], masked by 24 smallest bits==[16777215]==pow(2,24)-1
float bitORmask16777215(float a,float b
){return bitOR(a,b,vec4(1,2,4,8)      )+bitOR(a,b,vec4(1,2,4,8)*16.)
         +bitOR(a,b,vec4(1,2,4,8)*256. )+bitOR(a,b,vec4(1,2,4,8)*4096.)
         +bitOR(a,b,vec4(1,2,4,8)*65536.)+bitOR(a,b,vec4(1,2,4,8)*1048576.);}
//return bitwiseXOR of [a] and [b], masked by 23 smallest bits==[8388607]==pow(2,23)-1
float bitXORmask8388607(float a,float b
){return bitXOR(a,b,vec4(1,2,4,8)      )+bitXOR(a,b,vec4(1,2,4,8)*16.)
         +bitXOR(a,b,vec4(1,2,4,8)*256. )+bitXOR(a,b,vec4(1,2,4,8)*4096.)
         +bitXOR(a,b,vec4(1,2,4,8)*65536.)+bitXOR(a,b,vec3(1,2,4)*1048576.);}
         //note the last vec3() parameter lacks its *8



//bitwise shift type float [x>>n], only shifts whole part; assert a=floor(x) n=floor(n)
//float shiftsByN(float x,float n){return floor(x/pow(2.,n));}//dumb origin
//when it comes to shifting/rotation (by negative values), some contexts have ambiguous definitions.
//well, this definition uses exp2() it doesnt get more common than that?

float shift(float x,float n){return x*exp2(n);}
//a << b == a*exp2(b)
//a >> b == a/exp2(b)
//above 2 are precise, log2()|exp2() losslessly directly read mantissa|exponent
float bitOp(float a,float b,float o//assert a,b,o=floor(a,b,0); a,b domains[0..1]; o domain[0..15]
){return bitXORmask8388607((8./exp2(a))/exp2(2.*b),o);} //return (((8>>a)>>(2*b))&o;
