// Computing cos(x) like the CPUs do (iirc), using
// the fact that cos(x) â‰ˆ 1-xÂ²/2 as initial guess
// and cos(2x) = 2cosÂ²(x)-1 to refine. Brought up
// to me again partially by Walter Hacket IV.

// Yellow, GLSL cos(x)
// Red, doubling-formula cos(x)

// 0: basic form,        6 iterations
// 1: partial symmetrys, 3 iterations
// 2: full symmetry,     2 iterations
// 3: full symmetry,     2 iterations optimized
// 4: full symmetry,     4 iterations optimized
#define VERSION 0

const float kTau  = 6.2831853071;
const float kTau2 = 6.2831853071*0.5;
const float kTau4 = 6.2831853071*0.25;
const float kTau8 = 6.2831853071*0.125;

#if VERSION==0
float mcos( float x )
{
    x = mod(x,kTau);

    x /= 64.0;       // x = ð›¼/64
    x = 0.5*x*x-1.0; // cos( x) â‰ˆ 1-xÂ²/2

    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1

    return x;
}
#endif

#if VERSION==1
float mcos( float x )
{
    x = mod(x,kTau);

    bool negx = x>kTau2; if( negx ) x = x-kTau2;
    bool negy = x>kTau4; if( negy ) x = kTau2-x;

    x /= 8.0;        // x = ð›¼/8
    x = 0.5*x*x-1.0; // cos( x) â‰ˆ 1-xÂ²/2
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1

    return (negx^^negy) ? -x : x;
}
#endif

#if VERSION==2
float mcos( float x )
{
    x = mod(x,kTau);

    bool negx = x>kTau2; if( negx ) x = x-kTau2;
    bool negy = x>kTau4; if( negy ) x = kTau2-x;
    bool negz = x>kTau8; if( negz ) x = kTau4-x;

    x /= 4.0;        // x = ð›¼/4
    x = 0.5*x*x-1.0; // cos( x) â‰ˆ 1-xÂ²/2
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1
    x = 2.0*x*x-1.0; // cos(2x) = 2cosÂ²(x)-1

    if( negz )        x = sqrt(1.0-x*x);
    if( negx ^^ negy) x = -x;

    return x;
}
#endif

#if VERSION==3
float mcos( float x )
{
    x = mod(x,kTau);

    bool negx = x>kTau2; if( negx ) x = x-kTau2;
    bool negy = x>kTau4; if( negy ) x = kTau2-x;
    bool negz = x>kTau8; if( negz ) x = kTau4-x;

    x = x*x;              // MUL
    x = x*(1.0/32.0)-1.0; // MAD
    x = x*x;              // MUL
    x = x*x-x;            // MAD
    x = 8.0*x+1.0;        // MAD

    if( negz )        x = sqrt(1.0-x*x);
    if( negx ^^ negy) x = -x;

    return x;
}
#endif

#if VERSION==4
float mcos( float x )
{
    x = mod(x,kTau);

    bool negx = x>kTau2; if( negx ) x = x-kTau2;
    bool negy = x>kTau4; if( negy ) x = kTau2-x;
    bool negz = x>kTau8; if( negz ) x = kTau4-x;

    x = x*x;               // MUL
    x = x*(1.0/512.0)-1.0; // MAD
    x = x*x;               // MUL
    x = x*x-x;             // MAD
    x = 8.0*x+1.0;         // MAD
    x = x*x;               // MUL
    x = x*x-x;             // MAD
    x = 8.0*x+1.0;         // MAD

    if( negz )        x = sqrt(1.0-x*x);
    if( negx ^^ negy) x = -x;

    return x;
}
#endif


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2  p = fragCoord/iResolution.y;
    float px = 1.0/iResolution.y;

    vec3 col = vec3( 0.2 );

    // cos, yellow
    float y = 0.5+0.4*cos( 7.0*p.x );
    float dy = dFdx(y)/px;
    col = mix( col, vec3(1.0,1.0,0.0), 1.0 - smoothstep( 0.0, 2.0*px, abs(p.y-y)/sqrt(1.0+dy*dy)) );

    // mcos, red
    if( cos(iTime*kTau)>0.0 )
    {
    y = 0.5+0.4*mcos( 7.0*p.x );
    dy = dFdx(y)/px;
    col = mix( col, vec3(1.0,0.0,0.0), 1.0 - smoothstep( 0.0, 2.0*px, abs(p.y-y)/sqrt(1.0+dy*dy) ) );
    }

    fragColor = vec4( col, 1.0 );
}
