void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool hashedSeed = HashedSeedButton(iMouse.xy, iResolution.xy);

    vec2 uv = fragCoord.xy / iResolution.xy;

    // get random pixels that were rendered to BufferA
    vec4 bufB = texelFetch(iChannel0, ivec2(fragCoord.xy), 0);
    float col = bufB.x / bufB.w;

    // UI layout zones
	float orig = col;
    // Contrast enhance the random pixels labeled Mean x256
    if (uv.y >= spacing.y/0.125) col = (col-0.5)*256.0+0.5;
    if (fragCoord.y > iResolution.y * 0.8) {
        col = mix(0.5, 0.0, saturate((uv.y-0.865)*1024.0));
        // Little notches separating the different PRNG algo labels
    	if (fragCoord.y > iResolution.y * 0.82) col *= (fract(-uv.x / spacing.x)-0.95)*1000.0;
    }
    // Zero seed section
    if ((fragCoord.y > iResolution.y * 0.82) &&
        (fragCoord.y < iResolution.y * 0.84)) col = orig;
    // Little circles around each zone of random pixels
    vec2 rep = abs(fract((uv * vec2(1.0, 0.25))/spacing)-0.5)*2.0;
    if ((length(rep) > 1.15) && (fragCoord.y < iResolution.y * 0.8)) col = 0.5;
#ifndef BIT_BY_BIT_DEBUG
    // Left side bar
    if (uv.x < spacing.x) col = 0.5+(uv.x-spacing.x)/spacing.x*0.5;
#endif

    // ---- Button UI ----
    vec3 rgb = vec3(0.0);
    float buttonx = hashedSeed ? 0.42 : 0.0;
    vec3 buttonCol = vec3(0.1, 0.2, 0.6);
    if (iMouse.x == iMouse.z) buttonCol *= 2.0;
    float line = FillLine(uv, vec2(0.135,0.967),
                                    vec2(0.54, 0.967),
                                    vec2(0.0, 0.026), 0.02);
    rgb += buttonCol*(1.0-line);
    line = FillLine(uv, vec2(0.135 + 0.42,0.967),
                                    vec2(0.54 + 0.42, 0.967),
                                    vec2(0.0, 0.026), 0.02);
    rgb += buttonCol*(1.0-line);
    line = DrawOutline(uv, vec2(0.135 + buttonx,0.967),
                                    vec2(0.54 + buttonx, 0.967),
                                    vec2(0.0, 0.03), 0.02, 0.002);
    rgb += vec3(1.0, 1.0, 1.0)*(1.0-line);

    // Set up UV coords to be a letter grid for printing letters
    uv = fragCoord.xy / iResolution.xy;
    uv *= 15.0;
    uv.x *= 8.0/3.0;
    vec2 uvf = fract(uv);
    uvf.x = (uvf.x-0.5)*0.5+0.5;  // stretch letters on x axis
    vec2 uvi = floor(uv);

    // -------- print letters --------
    int letter = 32;
    if (floor(uvi.y) == 13.0) {
#ifndef BIT_BY_BIT_DEBUG
        // labels for each different PRNG algorithm
        letter = (letterArray[int(floor(uvi.x))]);
#else
        // Label each bit of the random state
        vec2 uvTemp = uv;
        uvTemp.x /= 40.0;
        uvTemp.x *= 32.0;
        uvi = floor(uvTemp);
        uvf = fract(uvTemp);
        letter = ((31 - int(floor(uvi.x))) % 10) + 48;
#endif
    }
    // button labels
    if (floor(uvi.y) == 14.0) {
        letter = (buttons[int(floor(uvi.x))]);
    }
    // Left side row labels
    else if (floor(uvi.y) == 2.0) {
    	if (floor(uvi.x) == 0.0) letter = (_R);
    	if (floor(uvi.x) == 1.0) letter = (_a);
    	if (floor(uvi.x) == 2.0) letter = (_n);
    	if (floor(uvi.x) == 3.0) letter = (_d);
    }
    else if (floor(uvi.y) == 10.0) {
    	if (floor(uvi.x) == 0.0) letter = (_M);
    	if (floor(uvi.x) == 1.0) letter = (_e);
    	if (floor(uvi.x) == 2.0) letter = (_a);
    	if (floor(uvi.x) == 3.0) letter = (_n);
    }
    else if (floor(uvi.y) == 9.0) {
    	if (floor(uvi.x) == 0.0) letter = (_x);
    	if (floor(uvi.x) == 1.0) letter = (_2);
    	if (floor(uvi.x) == 2.0) letter = (_5);
    	if (floor(uvi.x) == 3.0) letter = (_6);
    }
    else if (floor(uvi.y) == 6.0) {
    	if (floor(uvi.x) == 0.0) letter = (_S);
    	if (floor(uvi.x) == 1.0) letter = (_e);
    	if (floor(uvi.x) == 2.0) letter = (_q);
    }
    else if (floor(uvi.y) == 12.0) {
    	if (floor(uvi.x) == 0.0) letter = (_Z);
    	if (floor(uvi.x) == 1.0) letter = (_e);
    	if (floor(uvi.x) == 2.0) letter = (_r);
    	if (floor(uvi.x) == 3.0) letter = (_o);
    }

    // look up font texture
    vec2 charPos = vec2(letter % 16, (255-letter) / 16);
    vec4 tex = texture(iChannel1, (uvf + charPos) / 16.0f, -100.0);
	float letters = saturate(1.0-tex.w+0.4);
    rgb += 1.0-saturate((tex.w-0.48)*40.0);

    // Colorize fonts
    uv = fragCoord.xy / iResolution.xy;
#ifndef BIT_BY_BIT_DEBUG
    if ((uv.y >= 0.86) && (uv.y < 0.93)) {
        if (uv.x < spacing.x*2.0) rgb *= vec3(1.0, 0.1, 0.1);
        else if (uv.x < spacing.x*4.0) rgb *= vec3(0.4, 1.0, 0.1);
        else if (uv.x < spacing.x*7.0) rgb *= vec3(0.1, 0.7, 0.9);
        else if (uv.x < spacing.x*8.0) rgb *= vec3(1.0, 0.6, 0.1);
        else if (uv.x < spacing.x*9.0) rgb *= vec3(0.6, 0.3, 1.0);
        else rgb *= vec3(0.1, 0.3, 1.0);
    }
#endif
	rgb += col;
    rgb = saturate(rgb);

    //rgb = vec3(Hashf1i2(ivec2(fragCoord.xy-100.0)));
    // Gamma correct
    fragColor = vec4(sqrt(rgb), 1.0);
}

